    &lt;/Entry&gt;&#xD;
    &lt;Entry DisplayName="Non serializable fields" Priority="100"&gt;&#xD;
      &lt;Entry.Match&gt;&#xD;
        &lt;And&gt;&#xD;
          &lt;Kind Is="Field" /&gt;&#xD;
