@echo off

cd /d "%~dp0"

FOR %%c IN (Release Debug Azure) DO (
    dotnet build linq2db.sln -c %%c --no-incremental -v m
)

@echo off

cd /d "%~dp0"

FOR %%c IN (Release Debug Azure) DO (
    dotnet clean linq2db.sln -c %%c -v m
)

@echo off

cd /d "%~dp0"

FOR %%c IN (Release Debug) DO (
    dotnet clean Source\LinqToDB\LinqToDB.csproj -c %%c -v m
)
FOR %%c IN (Release Debug) DO (
    dotnet build Source\LinqToDB\LinqToDB.csproj -c %%c -v m
)

