{ lib
, buildDotnetModule
, fetchFromGitHub
, fetchurl
, gtk3
, libX11
, libXrandr
, libappindicator
, libevdev
, libnotify
, udev
, copyDesktopItems
, makeDesktopItem
, nixosTests
, wrapGAppsHook
, dpkg
, dotnetCorePackages

, strace

, git
, jq
, coreutils
, gawk
, gnused
, bash
}:

buildDotnetModule rec {
  pname = "OpenTabletDriver";
  version = "0.7.0.0";

  src = ./.;

  dotnet-sdk = dotnetCorePackages.sdk_8_0;
  dotnet-runtime = dotnetCorePackages.runtime_8_0;

  dotnetInstallFlags = [ "--framework=net8.0" ];

  projectFile = [ "OpenTabletDriver.Console" "OpenTabletDriver.Daemon" "OpenTabletDriver.UX.Gtk" ];
  nugetDeps = ./deps.nix;

  executables = [ "OpenTabletDriver.Console" "OpenTabletDriver.Daemon" "OpenTabletDriver.UX.Gtk" ];

  nativeBuildInputs = [
    copyDesktopItems
    wrapGAppsHook
    dpkg
    jq
    coreutils
    gawk
    gnused
    bash
  ];

  runtimeDeps = [
    gtk3
    libX11
    libXrandr
    libappindicator
    libevdev
    libnotify
    udev
  ];

  buildInputs = runtimeDeps;

  postFixup = ''
    # Give a more "*nix" name to the binaries
    mv $out/bin/OpenTabletDriver.Console $out/bin/otd
    mv $out/bin/OpenTabletDriver.Daemon $out/bin/otd-daemon
    mv $out/bin/OpenTabletDriver.UX.Gtk $out/bin/otd-gui

    install -Dm644 $src/OpenTabletDriver.UX/Assets/otd.png -t $out/share/pixmaps

    # Generate udev rules from source
    export OTD_CONFIGURATIONS="$src/OpenTabletDriver.Configurations/Configurations"

    mkdir -p $out/lib/udev/rules.d
    bash $src/generate-rules.sh \
      | sed 's@/usr/bin/env rm@${lib.getExe' coreutils "rm"}@' \
      > $out/lib/udev/rules.d/70-opentabletdriver.rules
  '';

  desktopItems = [
    (makeDesktopItem {
      desktopName = "OpenTabletDriver";
      name = "OpenTabletDriver";
      exec = "otd-gui";
      icon = "otd";
      comment = "Open source, cross-platform, user-mode tablet driver";
      categories = [ "Utility" ];
    })
  ];
}

{
  "nodes": {
    "nixpkgs": {
      "locked": {
        "lastModified": 1732014248,
        "narHash": "sha256-y/MEyuJ5oBWrWAic/14LaIr/u5E0wRVzyYsouYY3W6w=",
        "owner": "NixOS",
        "repo": "nixpkgs",
        "rev": "23e89b7da85c3640bbc2173fe04f4bd114342367",
        "type": "github"
      },
      "original": {
        "id": "nixpkgs",
        "ref": "nixos-unstable",
        "type": "indirect"
      }
    },
    "root": {
      "inputs": {
        "nixpkgs": "nixpkgs"
      }
    }
  },
  "root": "root",
  "version": 7
}

{
  description = "OpenTabletDriver is an open source, cross platform, user mode tablet driver.";

  inputs.nixpkgs.url = "nixpkgs/nixos-unstable";

  outputs = inputs @ { self, nixpkgs }: let
    inherit (nixpkgs.lib) attrValues genAttrs;

    supportedSystems = [ "x86_64-linux" "aarch64-linux" ];

    # Helper function to generate an attrset '{ x86_64-linux = f "x86_64-linux"; ... }'.
    forAllSystems = f: genAttrs supportedSystems (system: f system);

  in rec {
    # Nixpkgs instantiated for supported system types.
    nixpkgsFor = forAllSystems (system: import nixpkgs {
      inherit system;
      overlays = attrValues self.overlays;
    });

    # Nixpkgs overlay
    overlays.default = final: prev: {
      opentabletdriver = final.callPackage ./default.nix {};
    };

    # Provide binary packages for all system types.
    packages = forAllSystems (system: rec {
      inherit (nixpkgsFor.${system}) opentabletdriver;
      default = opentabletdriver;
    });

    # Provide a 'nix develop' environment for interactive hacking.
    devShells = forAllSystems (system: {
      default = import ./shell.nix {
        inherit system;
        flake = self;
      };
    });
  };
}

{ flake ? builtins.getFlake "path:${toString ./.}"
, system ? "x86_64-linux"
}:

let
  inherit (pkgs.lib) escapeShellArg makeLibraryPath;
  inherit (flake.packages.${system}) opentabletdriver;

  pkgs = flake.nixpkgsFor.${system};

in pkgs.mkShell {
  inputsFrom = [ opentabletdriver ];

  buildInputs = with pkgs; [
    # shellHook deps
    git
    # dev
    gnused
    gnugrep
    jq
  ];

  hardeningDisable = [ "all" ];

  shellHook = ''
    export LD_LIBRARY_PATH="${escapeShellArg (makeLibraryPath opentabletdriver.buildInputs)}"
    export REPO_ROOT="$(git rev-parse --show-toplevel)"

    if [ -d $REPO_ROOT ]; then
      export OTD_CONFIGURATIONS="$REPO_ROOT/OpenTabletDriver.Configurations/Configurations"
      export OTD_APPDATA="$REPO_ROOT/.data"
    fi
  '';
}
