{
  "version": "0.4.1",
  "publicReleaseRefSpec": [
    "^refs/heads/main", // we release out of master
    "^refs/heads/dev$", // we release out of develop
    "^refs/tags/v\\d+\\.\\d+" // we also release tags starting with vN.N
  ],
  "nugetPackageVersion":{
    "semVer": 2
  },
  "cloudBuild": {
    "buildNumber": {
      "enabled": true,
      "setVersionVariables": true
    }
  }
}
