{
  "name": "@serenity-is/workspace-serenity",
  "devDependencies": {
    "@vitest/browser": "v3.0.0-beta.3",
    "@vitest/coverage-v8": "v3.0.0-beta.3",
    "esbuild": "0.23.1",
    "flatpickr": "4.6.13",
    "jsx-dom": "8.1.6",
    "jsdom": "25.0.1",
    "playwright": "1.49.1",
    "typescript": "5.7.2",
    "vitest": "v3.0.0-beta.3"
  },
  "optionalDependencies": {
    "fsevents": "2.3.3"
  },
  "scripts": {
    "all": "pnpm i && pnpm -r build && pnpm -r tsc && pnpm -r test"
  },
  "private": true,
  "license": "MIT",
  "type": "module"
}

lockfileVersion: '9.0'

settings:
  autoInstallPeers: true
  excludeLinksFromLockfile: false

