﻿using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Diagnostics;
using System.IO;
using System.Runtime.InteropServices;
using Community.PowerToys.Run.Plugin.Everything.Properties;
using Wox.Plugin;
using Wox.Plugin.Logger;
using static Community.PowerToys.Run.Plugin.Everything.Interop.NativeMethods;

namespace Community.PowerToys.Run.Plugin.Everything
{
    internal sealed class Everything
    {
        private string exe = string.Empty;
        internal Everything(Settings setting)
        {
            Everything_SetRequestFlags(Request.FILE_NAME | Request.PATH);
            UpdateSettings(setting);
        }

        internal void UpdateSettings(Settings setting)
        {
            Everything_SetSort(setting.Sort);
            Everything_SetMax(setting.Max);
            Everything_SetMatchPath(setting.MatchPath);
            Everything_SetRegex(setting.RegEx);
            if (!string.IsNullOrEmpty(setting.EverythingPath))
            {
                if (setting.EverythingPath != exe && Path.Exists(setting.EverythingPath))
                    exe = setting.EverythingPath;
            }
            else if (string.IsNullOrEmpty(exe))
            {
                exe = Path.Exists("C:\\Program Files\\Everything 1.5a\\Everything64.exe") ? "C:\\Program Files\\Everything 1.5a\\Everything64.exe" :
                    (Path.Exists("C:\\Program Files\\Everything\\Everything.exe") ? "C:\\Program Files\\Everything\\Everything.exe" :
                    (Path.Exists("C:\\Program Files (x86)\\Everything 1.5a\\Everything.exe") ? "C:\\Program Files (x86)\\Everything 1.5a\\Everything.exe" :
                    (Path.Exists("C:\\Program Files (x86)\\Everything\\Everything.exe") ? "C:\\Program Files (x86)\\Everything\\Everything.exe" : string.Empty)));
            }
        }

        internal IEnumerable<Result> Query(string query, Settings setting)
        {
#if DEBUG
            if (setting.Log > LogLevel.None)
            {
                Debugger.Write($"\r\n\r\nNew Query: {query}\r\n" +
                    $"Prefix {setting.Prefix} | " +
                    $"Sort {(int)setting.Sort}_{Everything_GetSort()} | " +
                    $"Max {setting.Max}_{Everything_GetMax()} | " +
                    $"Match Path {setting.MatchPath}_{Everything_GetMatchPath()} | " +
                    $"Regex {setting.RegEx}_{Everything_GetRegex()}");
            }
#endif

            string orgqry = query;

            if (!string.IsNullOrEmpty(setting.Prefix))
                query = setting.Prefix + query;

            if (setting.EnvVar && orgqry.Contains('%'))
            {
                query = Environment.ExpandEnvironmentVariables(query).Replace(';', '|');
#if DEBUG
                if (setting.Log > LogLevel.None)
                    Debugger.Write($"EnvVariable\r\n{query}");
#endif
            }

            if (Everything_GetMinorVersion() < 5 && orgqry.Contains(':'))
            {
                foreach (var kv in setting.Filters)
                {
                    if (query.Contains(kv.Key, StringComparison.OrdinalIgnoreCase))
                    {
                        query = query.Replace(kv.Key, kv.Value);
#if DEBUG
                        if (setting.Log > LogLevel.None)
                            Debugger.Write($"Contains Filter: {kv.Key}\r\n{query}");
#endif
                    }
                }
            }

            Everything_SetSearchW(query);
            if (!Everything_QueryW(true))
            {
#if DEBUG
                if (setting.Log > LogLevel.None)
                    Debugger.Write("\r\nUnable to Query\r\n");
#endif
                throw new Win32Exception("Unable to Query");
            }

            uint resultCount = Everything_GetNumResults();
#if DEBUG
            if (setting.Log > LogLevel.None)
                Debugger.Write($"Results: {resultCount}");
#endif

            bool showMore = setting.ShowMore && !string.IsNullOrEmpty(exe) && resultCount == setting.Max;
            if (showMore)
            {
                var more = new Result()
                {
                    Title = Resources.more_results,
                    SubTitle = Resources.more_results_Subtitle,
                    IcoPath = "Images/Everything.light.png",
                    Action = e =>
                    {
                        using var process = new Process();
                        process.StartInfo.FileName = exe;
                        process.StartInfo.UseShellExecute = true;
                        process.StartInfo.Arguments = $@"-s ""{query.Replace("\"", "\"\"\"")}""";
                        try
                        {
                            process.Start();
                            return true;
                        }
                        catch (Win32Exception)
                        {
                            return false;
                        }
                    },
                    Score = int.MinValue,
                    QueryTextDisplay = orgqry,
                };
                yield return more;
            }

            for (uint i = 0; i < resultCount; i++)
            {
#if DEBUG
                if (setting.Log > LogLevel.None)
                    Debugger.Write($"\r\n===== RESULT #{i} =====");
#endif
                string name = Marshal.PtrToStringUni(Everything_GetResultFileNameW(i));
                string path = Marshal.PtrToStringUni(Everything_GetResultPathW(i));
                if (name == null || path == null)
                {
                    Log.Warn($"Result {i} is null for {name} and/or {path}, query: {query}", GetType());
                    continue;
                }

                string fullPath = Path.Combine(path, name);
#if DEBUG
                if (setting.Log > LogLevel.None)
                    Debugger.Write($"{fullPath.Length} {(setting.Log == LogLevel.Verbose ? fullPath : string.Empty)}");
#endif
                bool isFolder = Everything_IsFolderResult(i);
                if (isFolder)
                    path = fullPath;
                string ext = Path.GetExtension(fullPath.Replace(".lnk", string.Empty));
#if DEBUG
                if (setting.Log > LogLevel.None)
                    Debugger.Write($"Folder: {isFolder}\r\nFile Path {(setting.Log == LogLevel.Verbose ? path : path.Length)}\r\nFile Name {(setting.Log == LogLevel.Verbose ? name : name.Length)}\r\nExt: {ext}");
#endif
                var r = new Result()
                {
                    Title = name,
                    ToolTipData = new ToolTipData(name, fullPath),
                    SubTitle = Resources.plugin_name + ": " + fullPath,

                    IcoPath = isFolder ? "Images/folder.png" : (setting.Preview ?
                        fullPath : "Images/file.png"),
                    ContextData = new SearchResult()
                    {
                        Path = fullPath,
                        Title = name,
                        File = !isFolder,
                    },
                    Action = e =>
                    {
                        using var process = new Process();
                        process.StartInfo.FileName = fullPath;
                        process.StartInfo.WorkingDirectory = path;
                        process.StartInfo.UseShellExecute = true;

                        try
                        {
                            process.Start();
                            _ = Everything_IncRunCountFromFileName(fullPath);
                            return true;
                        }
                        catch (Win32Exception)
                        {
                            return false;
                        }
                    },

                    QueryTextDisplay = setting.QueryText ? (isFolder ? path : name) : orgqry,
                };
                yield return r;
            }
        }
    }
}

﻿using System;
using System.Collections.Generic;
using System.Linq;
using System.Reflection;
using System.Threading.Tasks;
using System.Windows.Controls;
using Community.PowerToys.Run.Plugin.Everything.Properties;
using Microsoft.PowerToys.Settings.UI.Library;
using Wox.Plugin;
using Wox.Plugin.Logger;
using static Community.PowerToys.Run.Plugin.Everything.Interop.NativeMethods;

namespace Community.PowerToys.Run.Plugin.Everything
{
    public class Main : IPlugin, IDisposable, IDelayedExecutionPlugin, IContextMenu, ISettingProvider, IPluginI18n
    {
        public static string PluginID => "A86867E2D932459CBD77D176373DD657";
        public string Name => Resources.plugin_name;
        public string Description => Resources.plugin_description;
        private readonly Settings _setting = new();
        private Everything _everything;
        private ContextMenuLoader _contextMenuLoader;
        private bool _disposed;

        public IEnumerable<PluginAdditionalOption> AdditionalOptions =>
        [
            new()
            {
                Key = nameof(Settings.Context),
                DisplayLabel = Resources.Context,
                DisplayDescription = Resources.Context_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Textbox,
                TextValue = _setting.Context,
            },
            new()
            {
                Key = nameof(Settings.Sort),
                DisplayLabel = Resources.Sort,
                DisplayDescription = Resources.Sort_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Combobox,
                ComboBoxItems = Enum.GetValues<Sort>().Cast<int>().Select(v => new KeyValuePair<string, string>(((Sort)v).ToString(), v + string.Empty)).ToList(),
                ComboBoxValue = (int)_setting.Sort,
            },
            new()
            {
                Key = nameof(Settings.Max),
                DisplayLabel = Resources.Max,
                DisplayDescription = Resources.Max_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Numberbox,
                NumberValue = _setting.Max,
            },
            new()
            {
                Key = nameof(Settings.Prefix),
                DisplayLabel = Resources.Prefix,
                DisplayDescription = Resources.Prefix_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Textbox,
                TextValue = _setting.Prefix,
            },
            new()
            {
                Key = nameof(Settings.EverythingPath),
                DisplayLabel = Resources.EverythingPath,
                DisplayDescription = Resources.EverythingPath_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Textbox,
                TextValue = _setting.EverythingPath,
            },
            new()
            {
                Key = nameof(Settings.CustomProgram),
                DisplayLabel = Resources.CustomProgram,
                DisplayDescription = Resources.CustomProgram_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Textbox,
                TextValue = _setting.CustomProgram,
            },
            new()
            {
                Key = nameof(Settings.CustomArg),
                DisplayLabel = Resources.CustomArg,
                DisplayDescription = Resources.CustomArg_Description,
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Textbox,
                TextValue = _setting.CustomArg,
            },
            new()
            {
                Key = nameof(Settings.Copy),
                DisplayLabel = Resources.SwapCopy,
                DisplayDescription = Resources.SwapCopy_Description,
                Value = _setting.Copy,
            },
            new()
            {
                Key = nameof(Settings.EnvVar),
                DisplayLabel = Resources.EnvVar,
                DisplayDescription = Resources.EnvVar_Description,
                Value = _setting.EnvVar,
            },
            new()
            {
                Key = nameof(Settings.MatchPath),
                DisplayLabel = Resources.Match_path,
                DisplayDescription = Resources.Match_path_Description,
                Value = _setting.MatchPath,
            },
            new()
            {
                Key = nameof(Settings.Preview),
                DisplayLabel = Resources.Preview,
                DisplayDescription = Resources.Preview_Description,
                Value = _setting.Preview,
            },
            new()
            {
                Key = nameof(Settings.QueryText),
                DisplayLabel = Resources.QueryText,
                DisplayDescription = Resources.QueryText_Description,
                Value = _setting.QueryText,
            },
            new()
            {
                Key = nameof(Settings.RegEx),
                DisplayLabel = Resources.RegEx,
                DisplayDescription = Resources.RegEx_Description,
                Value = _setting.RegEx,
            },
            new()
            {
                Key = nameof(Settings.ShowMore),
                DisplayLabel = Resources.ShowMore,
                DisplayDescription = Resources.ShowMore_Description,
                Value = _setting.ShowMore,
            },
            new()
            {
                Key = nameof(Settings.Updates),
                DisplayLabel = Resources.Updates,
                DisplayDescription = $"v{Assembly.GetExecutingAssembly().GetName().Version}",
                Value = _setting.Updates,
            },
#if DEBUG
            new()
            {
                Key = nameof(Settings.Log),
                DisplayLabel = "Debug Mode",
                PluginOptionType = PluginAdditionalOption.AdditionalOptionType.Combobox,
                ComboBoxItems = Enum.GetValues(typeof(LogLevel)).Cast<int>().Select(d => new KeyValuePair<string, string>(((LogLevel)d).ToString(), d + string.Empty)).ToList(),
                ComboBoxValue = (int)_setting.Log,
            },
#endif
        ];

        public void Init(PluginInitContext context)
        {
            if (_setting.Updates)
                Task.Run(() => new Update().UpdateAsync(Assembly.GetExecutingAssembly().GetName().Version, _setting));
            if (Everything_GetMinorVersion() < 5) _setting.Getfilters();
            _everything = new Everything(_setting);
            _contextMenuLoader = new ContextMenuLoader(context, _setting.Context);
            _contextMenuLoader.Update(_setting);
#if DEBUG
            if (_setting.Log > LogLevel.None)
                Debugger.Write("Init Complete\r\n");
#endif
        }

        public void UpdateSettings(PowerLauncherPluginSettings settings)
        {
            if (settings.AdditionalOptions != null)
            {
                _setting.Sort = (Sort)settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Sort)).ComboBoxValue;
                _setting.Max = (uint)settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Max)).NumberValue;
                _setting.Context = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Context)).TextValue;
                _setting.RegEx = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.RegEx)).Value;
                _setting.Preview = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Preview)).Value;
                _setting.MatchPath = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.MatchPath)).Value;
                _setting.Copy = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Copy)).Value;
                _setting.QueryText = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.QueryText)).Value;
                _setting.EnvVar = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.EnvVar)).Value;
                _setting.Updates = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Updates)).Value;
                _setting.Prefix = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Prefix)).TextValue;
                _setting.EverythingPath = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.EverythingPath)).TextValue;
                _setting.CustomProgram = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.CustomProgram)).TextValue;
                _setting.CustomArg = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.CustomArg)).TextValue;
                _setting.ShowMore = settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.ShowMore)).Value;
#if DEBUG
                _setting.Log = (LogLevel)settings.AdditionalOptions.FirstOrDefault(x => x.Key == nameof(_setting.Log)).ComboBoxValue;
#endif

                _everything?.UpdateSettings(_setting);
                _contextMenuLoader?.Update(_setting);
            }
        }

        public List<Result> Query(Query query)
        {
            List<Result> results = [];
            return results;
        }

        public List<Result> Query(Query query, bool delayedExecution)
        {
            List<Result> results = [];
            if (!string.IsNullOrEmpty(query.Search))
            {
                string searchQuery = query.Search;

                try
                {
                    results.AddRange(_everything.Query(searchQuery, _setting));
                }
                catch (System.ComponentModel.Win32Exception)
                {
                    results.Add(new Result()
                    {
                        Title = Resources.Everything_not_running,
                        SubTitle = Resources.Everything_ini,
                        IcoPath = "Images/warning.png",
                        Score = int.MaxValue,
                    });
                }
                catch (Exception e)
                {
#if DEBUG
                    if (_setting.Log > LogLevel.None)
                        Debugger.Write($"Everything Exception: {e.Message}\r\n{e.StackTrace}\r\n");
#endif

                    Log.Exception($"Everything Exception: {e.Message}\r\n{e.StackTrace}\r\n", e, GetType());
                }
            }

            return results;
        }

        protected virtual void Dispose(bool disposing)
        {
            if (!_disposed)
            {
                if (disposing)
                {
                }

                _disposed = true;
            }
        }

        public void Dispose()
        {
            // Do not change this code. Put cleanup code in 'Dispose(bool disposing)' method
            Dispose(disposing: true);
            GC.SuppressFinalize(this);
        }

        public List<ContextMenuResult> LoadContextMenus(Result selectedResult) => _contextMenuLoader.LoadContextMenus(selectedResult);
        public Control CreateSettingPanel() => throw new NotImplementedException();
        public string GetTranslatedPluginTitle() => Resources.plugin_name;
        public string GetTranslatedPluginDescription() => Resources.plugin_description;
    }
}

﻿using System;
using System.Collections.Generic;
using System.IO;
using System.Reflection;
using Wox.Plugin.Logger;
using static Community.PowerToys.Run.Plugin.Everything.Interop.NativeMethods;

namespace Community.PowerToys.Run.Plugin.Everything
{
    public class Settings
    {
        // Settings from PTR settings
        public Sort Sort { get; set; } = Sort.NAME_ASCENDING;
        public uint Max { get; set; } = 10;
        public string Context { get; set; } = "0123456";
        public bool Copy { get; set; }
        public bool MatchPath { get; set; }
        public bool Preview { get; set; } = true;
        public bool QueryText { get; set; }
        public bool RegEx { get; set; }
        public bool EnvVar { get; set; }
        public bool Updates { get; set; } = true;
        public string Skip { get; set; }
        public string Prefix { get; set; }
        public string EverythingPath { get; set; }
        public bool ShowMore { get; set; } = true;
        public string CustomProgram { get; set; } = "notepad.exe";
        public string CustomArg { get; set; } = "$P";
#if DEBUG
        public LogLevel Log { get; set; } = LogLevel.None;
#endif

        // Get Filters from settings.toml
        public Dictionary<string, string> Filters { get; } = [];
        internal void Getfilters()
        {
            Log.Info("User on Everything 1.4", GetType());
#if DEBUG
            if (Log > LogLevel.None)
                Debugger.Write("2.Getting Filters...");
#endif
            string[] strArr;
            try { strArr = File.ReadAllLines(Path.Combine(Path.GetDirectoryName(Assembly.GetExecutingAssembly().Location), "settings.toml")); }
            catch (Exception e)
            {
#if DEBUG
                if (Log > LogLevel.None)
                    Debugger.Write($"\r\nERROR: {e.Message}\r\n");
#endif
                Log.Error($"Error reading settings.toml: {e.Message}", GetType());
                return;
            }

            foreach (string str in strArr)
            {
                if (str.Length == 0 || str[0] == '#') continue;
                string[] kv = str.Split('=', StringSplitOptions.RemoveEmptyEntries | StringSplitOptions.TrimEntries);
                if (kv.Length != 2) continue;

                if (kv[0].Contains(':'))
                    Filters.TryAdd(kv[0].ToLowerInvariant(), kv[1] + (kv[1].EndsWith(';') ? ' ' : string.Empty));
            }
#if DEBUG
            if (Log > LogLevel.None)
                Debugger.Write(Log > LogLevel.Debug ? string.Join(Environment.NewLine, Filters) + "\r\n" : string.Empty + "  GettingFilters...Done");
#endif
        }
    }
#if DEBUG
    public enum LogLevel
    {
        None,
        Debug,
        Verbose,
    }
#endif
}

﻿using System;
using System.Diagnostics;
using System.Globalization;
using System.IO;
using System.Net.Http;
using System.Text;
using System.Text.Json;
using System.Threading.Tasks;
using System.Windows;
using Community.PowerToys.Run.Plugin.Everything.Properties;

namespace Community.PowerToys.Run.Plugin.Everything
{
    internal sealed class Update
    {
        private readonly CompositeFormat updateAvailable = CompositeFormat.Parse(Resources.UpdateAvailable);
        internal async Task UpdateAsync(Version v, Settings s)
        {
            string apiUrl = "https://api.github.com/repos/lin-ycv/EverythingPowerToys/releases/latest";
#if DEBUG
            if (s.Log > LogLevel.None)
                Debugger.Write("1.Checking Update...");
#endif
            try
            {
                using HttpClient httpClient = new();
                httpClient.DefaultRequestHeaders.UserAgent.ParseAdd("Mozilla/5.0");

                HttpResponseMessage response = await httpClient.GetAsync(apiUrl);
#if DEBUG
                if (s.Log == LogLevel.Verbose) Debugger.Write($"\tResponse: {response.StatusCode}");
#endif
                if (response.IsSuccessStatusCode)
                {
                    using JsonDocument jsonDocument = await JsonDocument.ParseAsync(await response.Content.ReadAsStreamAsync());
                    JsonElement root = jsonDocument.RootElement;
                    Version latest = Version.TryParse(root.GetProperty("tag_name").GetString().AsSpan(1), out var vNumber)
                        ? vNumber
                        : Version.Parse(root.GetProperty("tag_name").GetString());
                    if (latest > v && latest.ToString() != s.Skip)
                    {
                        MessageBoxResult mbox = MessageBox.Show(string.Format(CultureInfo.InvariantCulture, updateAvailable, v, latest), "Updater", MessageBoxButton.YesNoCancel);
                        if (mbox == MessageBoxResult.Yes && root.TryGetProperty("assets", out JsonElement assets))
                        {
                            string[] nameUrl = [string.Empty, string.Empty];
                            foreach (JsonElement asset in assets.EnumerateArray())
                            {
#if X64
                                if (asset.TryGetProperty("browser_download_url", out JsonElement downUrl) && downUrl.ToString().EndsWith("x64.exe", StringComparison.OrdinalIgnoreCase))
#elif ARM64
                                if (asset.TryGetProperty("browser_download_url", out JsonElement downUrl) && downUrl.ToString().EndsWith("ARM64.exe", StringComparison.OrdinalIgnoreCase))
#endif
                                {
                                    nameUrl[0] = asset.GetProperty("name").ToString();
                                    nameUrl[1] = downUrl.ToString();
                                }
                            }

                            if (nameUrl[0].Length > 0)
                            {
                                byte[] fileContent = await httpClient.GetByteArrayAsync(nameUrl[1]);
                                string fileName = Path.Combine(Path.GetTempPath(), nameUrl[0]);
                                File.WriteAllBytes(fileName, fileContent);
                                Process.Start(fileName);
                            }
                            else
                            {
                                ProcessStartInfo p = new("https://github.com/lin-ycv/EverythingPowerToys/releases/latest")
                                {
                                    UseShellExecute = true,
                                    Verb = "Open",
                                };
                                Process.Start(p);
                            }
                        }
                        else if (mbox == MessageBoxResult.No)
                        {
                            s.Skip = latest.ToString();
                        }
                    }
                }
            }
#if RELEASE
            catch
            { }
#else
            catch (Exception e)
            {
                if (s.Log > LogLevel.None)
                    Debugger.Write($"\r\nERROR: {e.Message}\r\n{e.StackTrace}\r\n");

            }
            if (s.Log > LogLevel.None)
                Debugger.Write("  Checking Update...Done");
#endif
        }
    }
}

