/// <summary>
/// Contains the repo identity public key when strong naming the assembly.
/// </summary>
internal static class StrongNamePublicKeys
{
  /// <summary>
  /// The public key used for assemblies in this repo (Key.snk).
  /// </summary>
  public const string DockerDotNetPublicKey = ", ";
}

{
  "$schema": "https://raw.githubusercontent.com/dotnet/Nerdbank.GitVersioning/master/src/NerdBank.GitVersioning/version.schema.json",
  "version": "3.125.15",
  "nugetPackageVersion": {
    "semVer": 2
  },
  "pathFilters": [
    // optional list of paths to consider when calculating version height.
  ],
  "publicReleaseRefSpec": [
    "^refs/heads/master$", // we release out of master
    "^refs/tags/v\\d+\\.\\d+" // we also release tags starting with vN.N
  ],
  "cloudBuild": {
    "setVersionVariables": true,
    "buildNumber": {
      "enabled": false,
      "includeCommitId": {
        "when": "nonPublicReleaseOnly",
        "where": "buildMetadata"
      }
    }
  },
  "release": {
    "branchName": "v{version}",
    "versionIncrement": "minor",
    "firstUnstableTag": "alpha"
  }
}
