{
  "metadata": [
    {
      "src": [
        {
          "files": [
            "src/Magicodes.ExporterAndImporter.Csv/**/*.cs",
            "src/Magicodes.ExporterAndImporter.Excel/**/*.cs",
            "src/Magicodes.ExporterAndImporter.PDF/**/*.cs",
            "src/Magicodes.ExporterAndImporter.HTML/**/*.cs",
            "src/Magicodes.ExporterAndImporter.Word/**/*.cs",
          ]
        }
      ],
      "dest": "api",
      "disableGitFeatures": false,
      "disableDefaultFilter": false
    }
  ],
  "build": {
    "content": [
      {
        "files": [
          "api/**.yml",
          "api/index.md"
        ]
      },
      {
        "files": [
          "articles/**.md",
          "articles/**/toc.yml",
          "toc.yml",
          "*.md"
        ]
      }
    ],
    "resource": [
      {
        "files": [
          "images/**",
          "res/**"
        ]
      }
    ],
    "overwrite": [
      {
        "files": [
          "apidoc/**.md"
        ],
        "exclude": [
          "obj/**",
          "_site/**"
        ]
      }
    ],
    "dest": "_site",
    "globalMetadataFiles": [],
    "fileMetadataFiles": [],
    "template": [
      "default"
    ],
    "postProcessors": [],
    "markdownEngineName": "markdig",
    "noLangKeyword": false,
    "keepFileLink": false,
    "cleanupCacheHistory": false,
    "disableGitFeatures": false
  }
}
