Shader "Hidden/Raymarch" {
SubShader {
Pass {
	ZWrite Off Fog { Mode Off }
	Blend Off
	Cull Back
	Stencil
	{
		Ref 0
		Comp equal
	}
	
CGPROGRAM
#include "UnityCG.cginc"
#include "Shared.cginc"
#pragma target 3.0
#pragma glsl
#pragma vertex vert_simple
#pragma fragment frag
#pragma multi_compile COLORED_ON COLORED_OFF
#pragma multi_compile ATTENUATION_CURVE_ON ATTENUATION_CURVE_OFF
#pragma multi_compile COOKIE_TEX_ON COOKIE_TEX_OFF
#pragma multi_compile DIRECTIONAL_SHAFTS SPOT_SHAFTS

sampler2D _CameraDepthTexture;
sampler2D _Coord;
sampler2D _Shadowmap;
sampler2D _ColorFilter;
sampler2D _AttenuationCurveTex;
sampler2D _Cookie;
float4 _ShadowmapDim;
float4 _ScreenTexDim;
float4 _LightColor;
float _Extinction;
float _Brightness;
float _MinDistFromCamera;

inline float attenuation(float distance)
{
	#if ATTENUATION_CURVE_ON
		return tex2Dlod (_AttenuationCurveTex, float4(distance, 0, 0, 0)).r;
	#else
		#if defined(DIRECTIONAL_SHAFTS)
			return exp(_Extinction * distance);
		#else
			return 1.0 / (1.0 + 25.0 * distance * distance);
		#endif
	#endif
}

inline float cookie(float2 pos)
{
	#if defined(COOKIE_TEX_ON)
		return tex2Dlod(_Cookie, float4(pos.xy, 0, 0)).w;
	#else
		float2 a = pos*2.0 - 1.0;
		return saturate(1.0 - pow(dot(a, a), 3.0));
	#endif
}

#ifdef SHADER_API_D3D9
#define MAX_STEPS 256
#else
#define MAX_STEPS 512
#endif

float4 frag(posuv i) : COLOR
{
	float2 uv = tex2D(_Coord, i.uv).xy;
	float2 uv05 = (floor(uv*_ScreenTexDim.xy) + 0.5)*_ScreenTexDim.zw;
	float sceneDepth = Linear01Depth(UNITY_SAMPLE_DEPTH(tex2D (_CameraDepthTexture, uv05)));

	float3 cameraPos = _CameraPosLocal.xyz;
	float near, far, rayLength;
	float3 rayN;

	// In the current space the light volume is either the full unit cube
	// centered at 0, or a frustum inscribed in that cube. We proceed the same,
	// except that for frustum pos.xy (shadowmap UVs) will need to be stretched out
	// closer to the near plane, to match shadowmap's perspective.
	// (We know we'll intersect the volume, since this shader shouldn't be run for
	// samples which don't.)
	IntersectVolume(uv, near, far, rayN, rayLength);

	sceneDepth *= rayLength;

	// Don't want to raymarch behind the camera, so clamp near intersection to 0.
	near = max(near, 0);
	near = max(near, _MinDistFromCamera);

	// The box is centered around 0,0,0. Offset it by 0.5 to
	// (0,0,0) (1,1,1) to make shadow sampling easier.
	cameraPos += 0.5;

	float3 frontPos = near*rayN + cameraPos;
	float depthAlongView = (min(sceneDepth, far) - near);
	float3 frontToBack = depthAlongView*rayN;

	// Number of steps is the length of frontToBack in shadowmap texels,
	// either along shadomap width or height, whichever projection is longer.
	float2 frontToBackTemp = floor (frontToBack.xy * _ShadowmapDim.xy);
	int steps = 2 * max(abs(frontToBackTemp.x), abs(frontToBackTemp.y));
	steps = clamp((fixed)steps, 4, MAX_STEPS);
	float oneOverSteps = 1.0 / float(steps);
	frontToBack.xy = frontToBackTemp.xy * _ShadowmapDim.zw;
	float3 frontToBackOverSteps = frontToBack*oneOverSteps;

	#if defined(SPOT_SHAFTS)
	float3 params = float3(0.5, 0.5 - _FrustumApex, - 0.5 - _FrustumApex);
	#endif

	float3 inscatter = 0;
	for (int i = 0; i < steps; i++)
	{
		float3 pos = frontPos + i * frontToBackOverSteps;
		#if defined(SPOT_SHAFTS)
		// stretch out UVs as we get closer to the near plane
		pos.xy = (pos.xy - params.x) * params.y / (pos.z + params.z) + params.x;
		#endif
		
		// Important to use tex2Dlod to save on calculating derivatives, and we're
		// sampling once every texel anyway. tex2D is 6x slower.
		float3 sample = tex2Dlod(_Shadowmap, float4(pos.xy, 0, 0)).x > pos.z;

		sample *= attenuation(pos.z);

		#if defined(COLORED_ON)
			sample *= tex2Dlod(_ColorFilter, float4(pos.xy, 0, 0)).xyz;
		#endif

		#if defined(SPOT_SHAFTS)
			sample *= cookie(pos.xy);
		#endif

		inscatter += sample;
	}

	// Normalize inscattered light depending on how many steps we took and what part
	// of the entire depth did we raymarch.
	inscatter *= _LightColor.rgb * _Brightness * oneOverSteps * depthAlongView;
	return saturate(inscatter).xyzz;
}

ENDCG
}
}
}

Shader "Hidden/Sample Positions" {
Properties {
}
SubShader {
Pass {
	Cull Off ZWrite Off ZTest Always Fog { Mode Off }

CGPROGRAM
#pragma target 5.0

#pragma vertex vert
#pragma fragment frag

#include "UnityCG.cginc"

sampler2D _Coord;
sampler2D _InterpolationEpi;
float4 _OutputTexDim;
float4 _CoordTexDim;
float _SampleType; // 0 - raymarched, 1 - interpolated along the epi ray
float4 _Color;
RWTexture2D<float4> _OutputTex;

struct v2f {
	float4 pos : POSITION;
	float2 uv : TEXCOORD0;
};

v2f vert (appdata_img v)
{
	v2f o;
	o.pos = v.vertex;
	o.uv = o.pos.xy * 0.5 + 0.5;
	o.uv.y = 1 - o.uv.y;
	return o;
}

half4 frag (v2f i) : COLOR
{
	int2 loc = floor(tex2D(_Coord, i.uv).xy*_OutputTexDim.xy);
	if (_SampleType == all(tex2D(_InterpolationEpi, i.uv).xy))
		_OutputTex[loc] = _Color;

	return 0;
}

ENDCG

}
}

Fallback Off
}

float4 _LightPos;

float2 GetEpipolarLineEntryPoint(float2 exit)
{
#if defined(LIGHT_ON_SCREEN)
	// If light is on screen, it's the entry point of every epipolar line
	return _LightPos.xy;

#else
	// If light is outside of the screen, the entry point is intersection of
	// the epipolar line with the screen edge

	float2 dir = exit.xy - _LightPos.xy;
	float distToExitEdge = length(dir);
	dir /= distToExitEdge;
	
	// Signed distances from light to interections with screen edges
	// (1 - validExit) to avoid division by 0
	bool4 validExit = abs(dir.xyxy) > 1e-5;
	float4 distToEdges = (float4(-1,-1,1,1) - _LightPos.xyxy) / (dir.xyxy + (1 - validExit));

	// Find which intersection is the one before exit - that will be the entry.
	// 3 other are just intersections with extended screen edges, outside of the screen.
	// TODO: Not sure about the 1e-3 offset here, maybe it should be resolution-dependent?
	validExit = validExit * (distToEdges < (distToExitEdge - 1e-3));
	// Workaround a compiler bug on osx with temp
	float4 temp = -(1 - validExit);
	temp *= 1.0+38;
	distToEdges = validExit * distToEdges + temp;

	float entryDist = 0;
	entryDist = max(entryDist, distToEdges.x);
	entryDist = max(entryDist, distToEdges.y);
	entryDist = max(entryDist, distToEdges.z);
	entryDist = max(entryDist, distToEdges.w);

	return _LightPos.xy + dir * entryDist;
#endif
}

// Every _InterpolationStep pixels we need to force a raymarched sample to sample the low freq changes of
// intensity along the epipolar line.
// Closer to the light we should make that minimal sampling more dense, due to higher gradient in light intensity,
// but in those directions the full raymarching takes fewer steps anyway.
float _InterpolationStep;

int GetInterpolationStep(float uvx)
{
	int step = _InterpolationStep;
	if ( uvx*8 < 1)
		step = step/4;

	return step;
}

struct appdata_pos
{
    float4 vertex : POSITION;
};

struct posuv
{
	float4 pos : SV_POSITION;
	float2 uv : TEXCOORD0;
};

posuv vert_simple (appdata_pos v)
{
	posuv o;
	o.pos = v.vertex;
	o.uv = o.pos.xy*0.5 + 0.5;
	#if !UNITY_UV_STARTS_AT_TOP
		o.pos.y *= -1;
	#endif
	o.uv.y = 1 - o.uv.y;
	
	return o;
}

// Cube() by Simon Green
inline bool Cube(float3 org, float3 dir, out float tnear, out float tfar)
{
	// compute intersection of ray with all six bbox planes
	float3 invR = 1.0 / dir;
	float3 tbot = invR * (- 0.5f - org);
	float3 ttop = invR * (  0.5f - org);
	
	// re-order intersections to find smallest and largest on each axis
	float3 tmin = min (ttop, tbot);
	float3 tmax = max (ttop, tbot);
	
	// find the largest tmin and the smallest tmax
	float2 t0 = max (tmin.xx, tmin.yz);
	tnear = max (t0.x, t0.y);
	t0 = min (tmax.xx, tmax.yz);
	tfar = min (t0.x, t0.y);

	// check for hit
	return tnear < tfar && tfar > 0;
}

// frustum inscribed in a unit cube centered at 0, apex on x
#define INF 9.9e8
inline bool Frustum(float3 org, float3 dir, float apex, out float near, out float far)
{
	float2 dirf = float2(0.5 - apex, 0.5); 
	float3 tbot, ttop;
	
	// intersection with near and far planes
	float invdirz = 1.0 / dir.z;
	tbot.z = invdirz * (-0.5 - org.z);
	ttop.z = invdirz * (0.5 - org.z);

	float temp = dirf.y * (org.z - apex);
	
	// intersection with inclined planes on y
	tbot.y = (-temp - dirf.x * org.y) / (dirf.x * dir.y + dirf.y * dir.z);
	ttop.y = ( temp - dirf.x * org.y) / (dirf.x * dir.y - dirf.y * dir.z);
	
	// intersection with inclined planes on x
	tbot.x = (-temp - dirf.x * org.x) / (dirf.x * dir.x + dirf.y * dir.z);
	ttop.x = ( temp - dirf.x * org.x) / (dirf.x * dir.x - dirf.y * dir.z);
	
	// if intersecting behind the apex, set t to ray's end
	float4 tempt = float4(tbot.xy, ttop.xy);
	tempt = lerp(tempt, INF * sign(dir.zzzz), step(org.zzzz + tempt * dir.zzzz, apex.xxxx));
	tbot.xy = tempt.xy;
	ttop.xy = tempt.zw;

	// re-order intersections to find smallest and largest on each axis
	float3 tmin = min(ttop, tbot);
	float3 tmax = max(ttop, tbot);
	
	// find the largest tmin and the smallest tmax
	float2 t0 = max(tmin.xx, tmin.yz);
	near = max(t0.x, t0.y);
	t0 = min(tmax.xx, tmax.yz);
	far = min(t0.x, t0.y);

	// check for hit
	return near < far && far > 0.0;
}

float4x4 _FrustumRays;
inline float3 FrustumRay(float2 uv, out float rayLength)
{
	float3 ray0 = lerp(_FrustumRays[0].xyz, _FrustumRays[1].xyz, uv.x);
	float3 ray1 = lerp(_FrustumRays[3].xyz, _FrustumRays[2].xyz, uv.x);
	float3 ray = lerp(ray0, ray1, uv.y);
	rayLength = length(ray);
	return ray/rayLength;
}

float4 _CameraPosLocal;
float _FrustumApex;
inline bool IntersectVolume(float2 uv, out float near, out float far, out float3 rayN, out float rayLength)
{
	rayN = FrustumRay(uv, rayLength);
	#if defined(DIRECTIONAL_SHAFTS)
	return Cube(_CameraPosLocal.xyz, rayN, near, far);
	#else
	return Frustum(_CameraPosLocal.xyz, rayN, _FrustumApex, near, far);
	#endif
}

