{
    "$schema": "https://gui-cs.github.io/Terminal.GuiV2Docs/schemas/tui-config-schema.json",
    "ConfigurationManager.ThrowOnJsonErrors": false,
    "Application.ArrangeKey": "Ctrl+F5",
    "Application.Force16Colors": false,  
    "Application.ForceDriver": "",
    "Application.IsMouseDisabled": false,
    "Application.NextTabGroupKey": "F6",
    "Application.NextTabKey": "Tab",
    "Application.PrevTabGroupKey": "Shift+F6",
    "Application.PrevTabKey": "Shift+Tab",
    "Application.QuitKey": "Esc",
    "ContextMenu.DefaultKey": "Shift+F10",
    "FileDialog.MaxSearchResults": 10000,
    "FileDialogStyle.DefaultUseColors": false,
    "FileDialogStyle.DefaultUseUnicodeCharacters": false,
    "Glyphs": {
        "CheckStateChecked": "✓"
    },
    "Theme": "Gruntled",
    "Themes": [
        {
            "Gruntled": {
                "ColorSchemes": [
                    {
                        "TopLevel": {
                            "Normal": {
                                "Foreground": "OrangeRed",
                                "Background": "AntiqueWhite"
                            },
                            "Focus": {
                                "Foreground": "White",
                                "Background": "Maroon"
                            },
                            "HotNormal": {
                                "Foreground": "DarkRed",
                                "Background": "AntiqueWhite"
                            },
                            "HotFocus": {
                                "Foreground": "Yellow",
                                "Background": "Maroon"
                            },
                            "Disabled": {
                                "Foreground": "LightPink",
                                "Background": "AntiqueWhite"
                            }
                        }
                    },
                    {
                        "Base": {
                            "Normal": {
                                "Foreground": "White",
                                "Background": "OrangeRed"
                            },
                            "Focus": {
                                "Foreground": "FireBrick",
                                "Background": "AntiqueWhite"
                            },
                            "HotNormal": {
                                "Foreground": "Gold",
                                "Background": "OrangeRed"
                            },
                            "HotFocus": {
                                "Foreground": "FireBrick",
                                "Background": "AntiqueWhite"
                            },
                            "Disabled": {
                                "Foreground": "LightPink",
                                "Background": "OrangeRed"
                            }
                        }
                    },
                    {
                        "Dialog": {
                            "Normal": {
                                "Foreground": "FireBrick",
                                "Background": "GhostWhite"
                            },
                            "Focus": {
                                "Foreground": "DarkGray",
                                "Background": "LightGray"
                            },
                            "HotNormal": {
                                "Foreground": "FireBrick",
                                "Background": "GhostWhite"
                            },
                            "HotFocus": {
                                "Foreground": "FireBrick",
                                "Background": "GhostWhite"
                            },
                            "Disabled": {
                                "Foreground": "Gray",
                                "Background": "DarkGray"
                            }
                        }
                    },
                    {
                        "Menu": {
                            "Normal": {
                                "Foreground": "White",
                                "Background": "FireBrick"
                            },
                            "Focus": {
                                "Foreground": "White",
                                "Background": "DarkRed"
                            },
                            "HotNormal": {
                                "Foreground": "Yellow",
                                "Background": "FireBrick"
                            },
                            "HotFocus": {
                                "Foreground": "Yellow",
                                "Background": "DarkRed"
                            },
                            "Disabled": {
                                "Foreground": "Gray",
                                "Background": "DarkGray"
                            }
                        }
                    },
                    {
                        "Error": {
                            "Normal": {
                                "Foreground": "Yellow",
                                "Background": "DarkRed"
                            },
                            "Focus": {
                                "Foreground": "DarkSalmon",
                                "Background": "Brown"
                            },
                            "HotNormal": {
                                "Foreground": "Black",
                                "Background": "DarkRed"
                            },
                            "HotFocus": {
                                "Foreground": "FireBrick",
                                "Background": "Brown"
                            },
                            "Disabled": {
                                "Foreground": "DarkGray",
                                "Background": "DarkRed"
                            }
                        }
                    }
                ]
            }
        }

    ]
}

