{
  lib,
  stdenv,
  fetchurl,
  cmake,
  qttools,
  qtbase,
  qtdeclarative,
  qtsvg,
  qtwayland,
  qtwebsockets,
  makeWrapper,
  wrapQtAppsHook,
  botan2,
  pkg-config,
  xvfb-run,
  installShellFiles,
}:

let
  pname = "qownnotes";
  appname = "QOwnNotes";
  #  version = builtins.head (builtins.match "#define VERSION \"([0-9.]+)\"" (builtins.readFile ./src/version.h));
  version = "local-build";
in
stdenv.mkDerivation {
  inherit pname appname version;

  src = builtins.path {
    path = ./src;
    name = "qownnotes";
  };

  nativeBuildInputs =
    [
      cmake
      qttools
      wrapQtAppsHook
      pkg-config
      installShellFiles
    ]
    ++ lib.optionals stdenv.hostPlatform.isLinux [ xvfb-run ]
    ++ lib.optionals stdenv.hostPlatform.isDarwin [ makeWrapper ];

  buildInputs = [
    qtbase
    qtdeclarative
    qtsvg
    qtwebsockets
    botan2
  ] ++ lib.optionals stdenv.hostPlatform.isLinux [ qtwayland ];

  cmakeFlags = [
    "-DQON_QT6_BUILD=ON"
    "-DBUILD_WITH_SYSTEM_BOTAN=ON"
  ];

  # Install shell completion on Linux (with xvfb-run)
  postInstall =
    lib.optionalString stdenv.hostPlatform.isLinux ''
      installShellCompletion --cmd ${appname} \
        --bash <(xvfb-run $out/bin/${appname} --completion bash) \
        --fish <(xvfb-run $out/bin/${appname} --completion fish)
      installShellCompletion --cmd ${pname} \
        --bash <(xvfb-run $out/bin/${appname} --completion bash) \
        --fish <(xvfb-run $out/bin/${appname} --completion fish)
    ''
    # Install shell completion on macOS
    + lib.optionalString stdenv.isDarwin ''
      installShellCompletion --cmd ${pname} \
        --bash <($out/bin/${appname} --completion bash) \
        --fish <($out/bin/${appname} --completion fish)
    ''
    # Create a lowercase symlink for Linux
    + lib.optionalString stdenv.hostPlatform.isLinux ''
      ln -s $out/bin/${appname} $out/bin/${pname}
    ''
    # Rename application for macOS as lowercase binary
    + lib.optionalString stdenv.hostPlatform.isDarwin ''
      # Prevent "same file" error
      mv $out/bin/${appname} $out/bin/${pname}.bin
      mv $out/bin/${pname}.bin $out/bin/${pname}
    '';

  meta = with lib; {
    description = "Plain-text file notepad and todo-list manager with Markdown support and Nextcloud/ownCloud integration";
    homepage = "https://www.qownnotes.org/";
    changelog = "https://www.qownnotes.org/changelog.html";
    downloadPage = "https://github.com/pbek/QOwnNotes/releases/tag/v${version}";
    license = licenses.gpl2Only;
    maintainers = with maintainers; [
      pbek
      totoroot
    ];
    platforms = platforms.unix;
  };
}

{
  "nodes": {
    "nixpkgs": {
      "locked": {
        "lastModified": 1722421184,
        "narHash": "sha256-/DJBI6trCeVnasdjUo9pbnodCLZcFqnVZiLUfqLH4jA=",
        "owner": "nixos",
        "repo": "nixpkgs",
        "rev": "9f918d616c5321ad374ae6cb5ea89c9e04bf3e58",
        "type": "github"
      },
      "original": {
        "owner": "nixos",
        "ref": "nixos-unstable",
        "repo": "nixpkgs",
        "type": "github"
      }
    },
    "nixpkgs-qt5153": {
      "locked": {
        "lastModified": 1655700315,
        "narHash": "sha256-N13cUhpyUlsbTugxwpUGrpaqJVLf4zsSZY1flJOO0kA=",
        "owner": "nixos",
        "repo": "nixpkgs",
        "rev": "72bbea9db7d727ed044e60b5f5febc60a3c5c955",
        "type": "github"
      },
      "original": {
        "owner": "nixos",
        "repo": "nixpkgs",
        "rev": "72bbea9db7d727ed044e60b5f5febc60a3c5c955",
        "type": "github"
      }
    },
    "root": {
      "inputs": {
        "nixpkgs": "nixpkgs",
        "nixpkgs-qt5153": "nixpkgs-qt5153"
      }
    }
  },
  "root": "root",
  "version": 7
}

{
  description = "QOwnNotes dev flake";

  inputs = {
    nixpkgs.url = "github:nixos/nixpkgs/nixos-unstable";
    nixpkgs-qt5153.url = "github:nixos/nixpkgs/72bbea9db7d727ed044e60b5f5febc60a3c5c955";
    #    systems.url = "github:nix-systems/default";
  };

  outputs =
    {
      self,
      nixpkgs,
      nixpkgs-qt5153,
    }:
    let
      system = "x86_64-linux";
      pkgs = nixpkgs.legacyPackages.${system};
      pkgs-qt5153 = nixpkgs-qt5153.legacyPackages.${system};
    in
    {
      packages.${system} = {
        qownnotes-qt6 = pkgs.qt6Packages.callPackage (import ./default.nix) { };
        qownnotes-qt5 = pkgs.libsForQt5.callPackage (import ./build-systems/nix/default-qt5.nix) { };
        qownnotes-qt5153 =
          pkgs-qt5153.libsForQt5.callPackage (import ./build-systems/nix/default-qt5.nix)
            { };
        default = pkgs.qt6Packages.callPackage (import ./default.nix) { };
      };

      devShell.x86_64-linux =
        with import nixpkgs {
          system = "x86_64-linux";
        };
        mkShell {
          nativeBuildInputs =
            with nixpkgs;
            with qt6;
            [
              gnumake
              crowdin-cli
              cmakeWithGui
              qmake
              qttools
              wrapQtAppsHook
              pkg-config

              # for ./build-systems/github/build-github-src.sh
              coreutils
              gh
            ];

          buildInputs =
            with nixpkgs;
            with qt6;
            [
              qtbase
              qtwebsockets
              qtdeclarative
              qtsvg
              botan2
            ];

          shellHook = ''
            echo "qmake:  $(qmake --version)"
          '';
        };
    };
}
