{
    "hooks": {},
    "git": {
        "changelog": null,
        "requireCleanWorkingDir": false,
        "requireUpstream": true,
        "requireCommits": true,
        "addUntrackedFiles": true,
        "commit": true,
        "commitMessage": "Version bump to ${version}",
        "commitArgs": [],
        "tag": true,
        "tagName": null,
        "tagAnnotation": "Release ${version}",
        "tagArgs": [],
        "push": true,
        "pushArgs": ["--follow-tags"],
        "pushRepo": "",
        "requireBranch": "master"
    },
    "github": {
        "release": true,
        "releaseName": "ghostwriter ${version}",
        "releaseNotes": "./releaseNotes.py ${version}",
        "preRelease": false,
        "draft": true,
        "tokenRef": "GITHUB_TOKEN",
        "assets": ["COPYING"],
        "host": null,
        "timeout": 0,
        "proxy": null,
        "skipChecks": false,
        "web": false
    },
    "plugins": {
        "@j-ulrich/release-it-regex-bumper": {
            "out": [
                {
                    "file": "ghostwriter.pro",
                    "search": "VERSION = .*",
                    "replace": "VERSION = {{version}}"
                },
                {
                    "file": "./resources/linux/ghostwriter.appdata.xml",
                    "search": "<release .*/>",
                    "replace": "<release version=\"{{version}}\" date=\"{{now:YYYY-MM-DD}}\" type=\"stable\"/>"
                },
                "./resources/linux/ghostwriter.1",
                {
                    "file": "./resources/linux/ghostwriter.1",
                    "search": "(\\.TH man \\d+) \".*?\" (.*)",
                    "replace": "$1 \"{{now:D MMM YYYY}}\" $2"
                },
                {
                    "file": "ghostwriter.spec",
                    "search": "%global changelog_date .*",
                    "replace": "%global changelog_date {{now:ddd MMM DD YYYY}}"
                },
                {
                    "file": "ghostwriter.spec",
                    "search": "%global appver .*",
                    "replace": "%global appver {{version}}"
                },
                "./debian/changelog",
                {
                    "file": "./debian/changelog",
                    "search": "(\\s+--\\s+.*?>).*",
                    "replace": "$1  {{now:ddd, DD MMM YYYY HH:mm:ss ZZ}}"
                },
                {
                    "file": "appveyor.yml",
                    "search": "version:.*",
                    "replace": "version: {{version}}+{build}"
                },
                {
                    "file": "appveyor.yml",
                    "search": "appver:.*",
                    "replace": "appver: {{version}}"
                },
                {
                    "file": "CHANGELOG.md",
                    "search": "\\[Unreleased\\].*",
                    "replace": "[{{version}}] - {{now:YYYY-MM-DD}}"
                }
            ]
        }
    }
}

{
    "version": 2,
    "configurePresets": [
        {
            "name": "dev",
            "displayName": "Build as debug",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "Debug",
                "CMAKE_EXPORT_COMPILE_COMMANDS": "ON"
            }
        },
        {
            "name": "dev-disable-deprecated",
            "displayName": "Build as without deprecated methods",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build-disable-deprecated",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "Debug",
                "CMAKE_EXPORT_COMPILE_COMMANDS": "ON",
                "CMAKE_CXX_FLAGS_INIT": "-DQT_DISABLE_DEPRECATED_BEFORE=0x060000 -DKF_DISABLE_DEPRECATED_BEFORE_AND_AT=0x060000"
            }
        },
        {
            "name": "asan",
            "displayName": "Build with Asan support.",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build-asan",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "Debug",
                "ECM_ENABLE_SANITIZERS" : "'address;undefined'",
                "CMAKE_EXPORT_COMPILE_COMMANDS": "ON"
            }
        },
        {
            "name": "unity",
            "displayName": "Build with CMake unity support.",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build-unity",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "Debug",
                "CMAKE_UNITY_BUILD": "ON",
                "CMAKE_EXPORT_COMPILE_COMMANDS": "ON"
            }
        },
        {
            "name": "release",
            "displayName": "Build as release mode.",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build-release",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "Release"
            }
        },
        {
            "name": "profile",
            "displayName": "profile",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build-profile",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "RelWithDebInfo",
                "CMAKE_EXPORT_COMPILE_COMMANDS": "ON"
            }
        },
        {
            "name": "clazy",
            "displayName": "clazy",
            "generator": "Ninja",
            "binaryDir": "${sourceDir}/build-clazy",
            "cacheVariables": {
                "CMAKE_BUILD_TYPE": "Debug"
            },
            "environment": {
                "CXX": "clazy",
                "CCACHE_DISABLE": "ON"
            }
        }
    ],
    "buildPresets": [
        {
            "name": "dev",
            "configurePreset": "dev"
        },
        {
            "name": "asan",
            "configurePreset": "asan"
        },
        {
            "name": "dev-disable-deprecated",
            "configurePreset": "dev-disable-deprecated"
        },
        {
            "name": "unity",
            "configurePreset": "unity"
        },
        {
            "name": "clazy",
            "configurePreset": "clazy",
            "environment": {
                "CLAZY_CHECKS" : "level0,level1,detaching-member,ifndef-define-typo,isempty-vs-count,qrequiredresult-candidates,reserve-candidates,signal-with-return-value,unneeded-cast,function-args-by-ref,function-args-by-value,returning-void-expression,no-ctor-missing-parent-argument,isempty-vs-count,qhash-with-char-pointer-key,raw-environment-function,qproperty-type-mismatch,old-style-connect,qstring-allocations,container-inside-loop,heap-allocated-small-trivial-type,inefficient-qlist,qstring-varargs,level2,detaching-member,heap-allocated-small-trivial-type,isempty-vs-count,qstring-varargs,qvariant-template-instantiation,raw-environment-function,reserve-candidates,signal-with-return-value,thread-with-slots,no-ctor-missing-parent-argument,no-missing-typeinfo",
                "CCACHE_DISABLE" : "ON"
            }
        }
    ]
}

