void CodeGenerator::InsertArg(const ParenListExpr* stmt)
{
    for(OnceFalse needsComma{}; const auto& expr : stmt->children()) {
        mOutputFormatHelper.AppendComma(needsComma);

        InsertArg(expr);
    }
}
