static bool IsConstQualifiedType(QualType type)
{
    if(not type.isNull()) {
        if(auto* typePtr = type.getTypePtrOrNull()) {
            if(auto pointee = typePtr->getPointeeType(); not pointee.isNull()) {
                return pointee.isConstQualified();
            }
        }
    }

    return false;
}
