		}
	}
	if (!files.empty()) {
		report_info("saving dive data to %s", join(files, ", ").c_str());
		save_dives(files.front().c_str());
	} else {
		printf("No log files given, not saving dive data.\n");
		printf("Give a log file name as argument, or configure a cloud URL.\n");
	}
