{
  "version": 3,
  "configurePresets": [
    {
      "name": "cmake-plugin-minimal",
      "displayName": "CMake, C++, Git, Debug minimal Build",
      "generator": "Ninja",
      "hidden" : true,
      "cacheVariables": {
        "BUILD_PLUGINS": "Core;Designer;DiffEditor;TextEditor;ProjectExplorer;CppEditor;CodePaster;Docker;Git;Help;QmakeProjectManager;CMakeProjectManager;ClangCodeModel;ClangTools;ClangFormat;Debugger;QtSupport;ResourceEditor;VcsBase;Welcome;LanguageClient;RemoteLinux",
        "BUILD_EXECUTABLES": "QtCreator;qtcreator_ctrlc_stub;qtcreator_process_stub;win64interrupt",
        "WITH_QMLDESIGNER": "OFF"
      }
    }
  ]
}

Project {
    name: "Qt Creator"
    minimumQbsVersion: "2.0.0"
    property path ide_source_tree: path
    property pathList additionalPlugins: []
    property pathList additionalLibs: []
    property pathList additionalTools: []
    property pathList additionalAutotests: []
    property string sharedSourcesDir: path + "/src/shared"
    qbsSearchPaths: "qbs"

    references: [
        "doc/doc.qbs",
        "src/src.qbs",
        "share/share.qbs",
        "share/qtcreator/translations/translations.qbs",
        "tests/tests.qbs"
    ]

    Product {
        name: "cmake project files"
        files: {
            var patterns = ["**/CMakeLists.txt", "**/*.cmake", "**/*.cmake.in"];
            var list = [].concat(patterns);
            var props = [additionalPlugins, additionalLibs, additionalTools, additionalAutotests];
            for (var i = 0; i < props.length; ++i) {
                for (var j = 0; j < props[i].length; ++j) {
                    for (var k = 0; k < patterns.length; ++k)
                        list.push(props[i][j] + "/" + patterns[k]);
                }
            }
            return list;
        }
    }
}
