              'outputs': [
                '<(PRODUCT_DIR)/crosswalk-<(xwalk_version)-1_<(deb_arch).deb',
              ],
              'action': [ '<@(deb_cmd)', ],
            },
          ],
        },
      ],
    }],
  ],
