{
  "version": 3,
  "configurePresets": [
    {
      "name": "base",
      "hidden": true,
      "generator": "Ninja",
      "binaryDir": "${sourceDir}/build",
      "toolchainFile": "$env{VCPKG_ROOT}/scripts/buildsystems/vcpkg.cmake",
      "condition": {
        "type": "equals",
        "lhs": "${hostSystemName}",
        "rhs": "Windows"
      },
      "cacheVariables": {
        "KICAD_BUILD_QA_TESTS": "OFF",
        "KICAD_BUILD_I18N": "ON",
        "KICAD_SCRIPTING_WXPYTHON": "ON"
      }
    },
    {
      "name": "win64",
      "hidden": true,
      "cacheVariables": {
        "KICAD_WIN32_DPI_AWARE": "ON",
        "CMAKE_INSTALL_PREFIX": "out",
        "CMAKE_PDB_OUTPUT_DIRECTORY": "out/pdb"
      },
      "architecture": {
        "value": "x64",
        "strategy": "external"
      },
      "inherits": [
        "base"
      ]
    },
    {
      "name": "x64-debug",
      "displayName": "x64 Debug",
      "description": "Sets debug build type and x64 arch",
      "inherits": "win64",
      "cacheVariables": {
        "CMAKE_BUILD_TYPE": "Debug"
      }
    },
    {
      "name": "x64-release",
      "displayName": "x64 Release",
      "description": "Sets release build type",
      "inherits": "win64",
      "cacheVariables": {
        "CMAKE_BUILD_TYPE": "RelWithDebInfo"
      }
    }
  ]
}

{
  "environments": [
    {
      "BuildDir": "${workspaceRoot}\\build",
      "InstallDir": "${workspaceRoot}\\build\\install"
    },
    {
      "environment": "vcpkg",
      "VcPkgDir": "D:/vcpkg/"
    }
  ],
  "configurations": [
    {
      "name": "x64-Debug",
      "generator": "Ninja",
      "configurationType": "Debug",
      "inheritEnvironments": [ "msvc_x64_x64", "vcpkg" ],
      "buildRoot": "${env.BuildDir}\\${name}",
      "installRoot": "${env.InstallDir}\\${name}",
      "cmakeCommandArgs": "",
      "buildCommandArgs": "-v",
      "ctestCommandArgs": "",
      "variables": [
        {
          "name": "KICAD_BUILD_QA_TESTS",
          "value": "False",
          "type": "BOOL"
        },
        {
          "name": "KICAD_WIN32_DPI_AWARE",
          "value": "ON",
          "type": "BOOL"
        }
      ],
      "cmakeToolchain": "${env.VcPkgDir}/scripts/buildsystems/vcpkg.cmake"
    },
    {
      "name": "x64-Release",
      "generator": "Ninja",
      "configurationType": "RelWithDebInfo",
      "inheritEnvironments": [ "msvc_x64_x64", "vcpkg" ],
      "buildRoot": "${env.BuildDir}\\${name}",
      "installRoot": "${env.InstallDir}\\${name}",
      "cmakeCommandArgs": "",
      "buildCommandArgs": "-v",
      "ctestCommandArgs": "",
      "variables": [
        {
          "name": "KICAD_BUILD_QA_TESTS",
          "value": "False",
          "type": "BOOL"
        },
        {
          "name": "KICAD_WIN32_DPI_AWARE",
          "value": "ON",
          "type": "BOOL"
        }
      ],
      "cmakeToolchain": "${env.VcPkgDir}/scripts/buildsystems/vcpkg.cmake"
    }
  ]
}

{
  "$schema": "https://raw.githubusercontent.com/microsoft/vcpkg-tool/main/docs/vcpkg-configuration.schema.json",
  "default-registry": {
    "kind": "git",
    "repository": "https://github.com/microsoft/vcpkg",
    "baseline": "ea189a0a823a17dca94f6af61d09429f2f45502c"
  },
  "registries": [
    {
      "kind": "git",
      "repository": "https://gitlab.com/kicad/packaging/kicad-vcpkg-registry.git",
      "baseline": "71ee41eab29882214dadf233296fa8358af4e2c0",
      "packages": [
        "glew",
        "python3",
        "opencascade",
        "wxpython",
        "wxwidgets",
        "ngspice"
      ]
    }
  ]
}
