var glob = require('glob');
var fs = require('fs');
var path = require('path');
var os = require('os');

require('find-java-home')(function(err, home){
  var dll;
  var dylib;
  var so,soFiles;
  var binary;

  if(home){
    dll = glob.sync('**/jvm.dll', {cwd: home})[0];
    dylib = glob.sync('**/libjli.dylib', {cwd: home})[0];
    soFiles = glob.sync('**/libjvm.so', {cwd: home});

    if(soFiles.length>0)
      so = getCorrectSoForPlatform(soFiles);

    binary = dll || dylib || so;

    fs.writeFileSync(
      path.resolve(__dirname, './build/jvm_dll_path.json'),
      binary
      ? JSON.stringify(
          path.delimiter
          + path.dirname(path.resolve(home, binary))
        )
      : '""'
    );
  }
});

function getCorrectSoForPlatform(soFiles){
  var so = _getCorrectSoForPlatform(soFiles);
  if (so) {
    so = removeDuplicateJre(so);
  }
  return so;
}

function removeDuplicateJre(filePath){
  while(filePath.indexOf('jre/jre')>=0){
    filePath = filePath.replace('jre/jre','jre');
  }
  return filePath;
}

function _getCorrectSoForPlatform(soFiles){

  var architectureFolderNames = {
    'ia32': 'i386',
    'x64': 'amd64'
  };

  if(os.platform() != 'sunos')
    return soFiles[0];

  var requiredFolderName = architectureFolderNames[os.arch()];

  for (var i = 0; i < soFiles.length; i++) {
    var so = soFiles[i];

    if(so.indexOf('server')>0)
      if(so.indexOf(requiredFolderName)>0)
        return so;
  }

  return soFiles[0];
}

var java = require("./");
java.options.push("-Djava.awt.headless=true");
//java.options.push('-agentlib:jdwp=transport=dt_socket,server=y,suspend=y,address=5005');

java.classpath.push("test/");
java.classpath.push("test/commons-lang3-3.1.jar");
java.classpath.push("test8/");

function promisifyQ(f) {
  // Q doesn't provide a promisify function that works directly on a method.
  // The .denodeify() (aka .nfbind()) function requires a bound function.
  return function(/* arguments */) {
    return require('q').nbind(f, this).apply(undefined, arguments);
  }
}

java.asyncOptions = {
  syncSuffix: "Sync",
  asyncSuffix: "",
  promiseSuffix: 'Promise',
  promisify: require('when/node').lift         // https://github.com/cujojs/when


// We've tested with 5 different Promises/A+ implementations:
//   promisify: require('bluebird').promisify     // https://github.com/petkaantonov/bluebird/
//   promisify: require('promise').denodeify      // https://github.com/then/promise
//   promisify: require('vow-node').promisify     // https://github.com/dfilatov/vow-node
//   promisify: require('when/node').lift         // https://github.com/cujojs/when
//   promisify: promisifyQ                        // https://github.com/kriskowal/q requires wrapper promisifyQ.
};

module.exports.java = java;

// testRunner.js

// This is a custom test runner. All tests are run with nodeunit, but in separate
// processes, which allows us to test java with different configuration options.

var async = require('async');
var chalk = require('chalk');
var childProcess = require('child_process');
var glob = require('glob');
var path = require('path');

var tests = glob.sync(path.join('testAsyncOptions', '*.js'));

tests.unshift('test test8');  // Arrange to run the primary tests first, in a single process

function runTest(testArgs, done) {
  var cmd = 'node_modules/.bin/nodeunit ';
  if(process.platform == "win32")
    cmd = 'node_modules\\.bin\\nodeunit ';  
  cmd += testArgs;
  childProcess.exec(cmd, function (error, stdout, stderr) {
    // It appears that nodeunit merges error output into the stdout
    // so these three lines are probably useless.
    var errText = stderr.toString();
    if (errText !== '')
      console.error(chalk.bold.red(errText));

    process.stdout.write(stdout.toString());
    done(error);
  });
}

async.eachSeries(tests, runTest, function(err) {
  if (err) {
    console.error(chalk.bold.red(err));
    process.exit(1);
  }
});
