import test from 'ava';
import libui from '.';

test('new UiWindow - throw if too few arguments', t => {
	const err = t.throws(() => new libui.UiWindow('Test window'));
	t.true(err instanceof Error);
	t.is(err.message, 'Wrong number of arguments');
});

function checkEvent(
	Class,
	eventName,
	propertyName,
	propertyType,
	builder = () => new Class()
) {
	return [
		`Widget ${Class.name} has event ${eventName}`,
		t => {
			let emitted = false;

			const widget = builder();
			widget[eventName](() => {
				emitted = true;
			});

			if (propertyType === Boolean) {
				widget[propertyName] = true;
			} else if (propertyType === String) {
				widget[propertyName] = 'some value';
			} else if (propertyType === Number) {
				widget[propertyName] = 2;
			} else if (propertyType === libui.Color) {
				widget[propertyName] = new libui.Color(1, 2, 3, 0.4);
			}
			t.true(emitted);
		}
	];
}

function checkProperty(Class, propertyName, type, builder = () => new Class()) {
	return [
		`Widget ${Class.name} has property ${propertyName}`,
		t => {
			const widget = builder();
			const setterName =
				'set' + propertyName[0].toUpperCase() + propertyName.slice(1);
			const getterName =
				'get' + propertyName[0].toUpperCase() + propertyName.slice(1);
			const setter = widget[setterName].bind(widget);
			const getter = widget[getterName].bind(widget);
			t.true(typeof getter === 'function');
			t.true(typeof setter === 'function');

			if (type === Boolean) {
				setter(true);
				t.true(getter());
				t.true(widget[propertyName]);

				setter(false);
				t.false(getter());
				t.false(widget[propertyName]);

				widget[propertyName] = true;
				t.true(getter());

				widget[propertyName] = false;
				t.false(getter());
			}

			if (type === String) {
				setter('some value');
				t.is(widget[propertyName], 'some value');
				t.is(getter(), 'some value');

				setter('');
				t.is(getter(), '');
				t.is(widget[propertyName], '');

				widget[propertyName] = 'some value';
				t.is(getter(), 'some value');

				widget[propertyName] = '';
				setter('');
				t.is(getter(), '');
			}

			if (type === libui.Color) {
				setter(new libui.Color(1, 2, 3, 0.4));
				const c1 = widget[propertyName];
				t.is(c1.r, 1);
				t.is(c1.g, 2);
				t.is(c1.b, 3);
				t.is(c1.a, 0.4);
				const c2 = getter();
				t.is(c2.r, 1);
				t.is(c2.g, 2);
				t.is(c2.b, 3);
				t.is(c2.a, 0.4);

				setter(new libui.Color(3, 1, 2, 0.5));
				const c3 = widget[propertyName];
				t.is(c3.r, 3);
				t.is(c3.g, 1);
				t.is(c3.b, 2);
				t.is(c3.a, 0.5);
				const c4 = getter();
				t.is(c4.r, 3);
				t.is(c4.g, 1);
				t.is(c4.b, 2);
				t.is(c4.a, 0.5);

				widget[propertyName] = new libui.Color(1, 2, 3, 0.4);
				const c5 = widget[propertyName];
				t.is(c5.r, 1);
				t.is(c5.g, 2);
				t.is(c5.b, 3);
				t.is(c5.a, 0.4);
				const c6 = getter();
				t.is(c6.r, 1);
				t.is(c6.g, 2);
				t.is(c6.b, 3);
				t.is(c6.a, 0.4);

				widget[propertyName] = new libui.Color(3, 1, 2, 0.5);
				const c7 = widget[propertyName];
				t.is(c7.r, 3);
				t.is(c7.g, 1);
				t.is(c7.b, 2);
				t.is(c7.a, 0.5);
				const c8 = getter();
				t.is(c8.r, 3);
				t.is(c8.g, 1);
				t.is(c8.b, 2);
				t.is(c8.a, 0.5);
			}

			if (type === Number) {
				setter(2);
				t.is(getter(), 2);
				t.is(widget[propertyName], 2);

				setter(0);
				t.is(getter(), 0);
				t.is(widget[propertyName], 0);

				widget[propertyName] = 2;
				t.is(getter(), 2);

				widget[propertyName] = 0;
				t.is(getter(), 0);
			}
		}
	];
}

