  if (!is_debug) {
    if (dcheck_always_on) {
      _common_web_test_args += [ "--timeout-ms=12000" ]
    } else if (v8_enable_debugging_features) {
      # In a pure release build with a debug V8, tests run slower.
      _common_web_test_args += [ "--timeout-ms=30000" ]
