{
  "version": 4,
  "cmakeMinimumRequired":
  {
    "major": 3,
    "minor": 27,
    "patch": 2
  },
  "configurePresets":
  [
    {
      "name": "Common",
      "generator": "Ninja",
      "binaryDir": "${sourceDir}/Build/${presetName}",
      "installDir": "${sourceDir}/Install/${presetName}",
      "hidden": true
    },
    {
      "name": "Linux-Common",
      "inherits": "Common",
      "cacheVariables":
      {
        "CMAKE_TOOLCHAIN_FILE": "${sourceDir}/CMake/LinuxToolchain.cmake"
      },
      "hidden": true
    },
    {
      "name": "Windows-Debug",
      "inherits": "Common",
      "cacheVariables":
      {
        "CMAKE_BUILD_TYPE": "Debug"
      }
    },
    {
      "name": "Windows-Development",
      "inherits": "Common",
      "cacheVariables":
      {
        "CMAKE_BUILD_TYPE": "RelWithDebInfo"
      }
    },
    {
      "name": "Windows-Release",
      "inherits": "Common",
      "cacheVariables":
      {
        "CMAKE_BUILD_TYPE": "Release"
      }
    },
    {
      "name": "Linux-Debug",
      "inherits": "Linux-Common",
      "cacheVariables":
      {
        "CMAKE_BUILD_TYPE": "Debug"
      }
    },
    {
      "name": "Linux-Development",
      "inherits": "Linux-Common",
      "cacheVariables":
      {
        "CMAKE_BUILD_TYPE": "RelWithDebInfo"
      }
    },
    {
      "name": "Linux-Release",
      "inherits": "Linux-Common",
      "cacheVariables":
      {
        "CMAKE_BUILD_TYPE": "Release"
      }
    }
  ]
}

pipeline
{
    stages
    {
        stage('Ubuntu')
        {
            agent { label "ubuntu_22_04" }
            stages {
                stage('Configure')
                {
                    steps {
                        sh "cmake -G Ninja -S . -B Build --toolchain=$PWD/CMake/LinuxToolchain.cmake -DLAUNCH_ARGS=-prefernvidia -DCMAKE_BUILD_TYPE=Release -DENABLE_ROS2=ON -DBUILD_CARLA_UNREAL=ON -DCARLA_UNREAL_ENGINE_PATH=$CARLA_UNREAL_ENGINE_PATH"
                    }
                }
                stage('Build Python API')
                {
                    steps {
                        sh "cmake --build Build --target package"
                    }
                }
                stage('Build CARLA')
                {
                    steps {
                        sh "cmake --build Build --target carla-unreal"
                    }
                }
                stage('Package')
                {
                    steps {
                        sh "cmake --build Build --target package"
                    }
                }
            }
        }
    }
}
