                "disable_filters": true,
                "disable_summary_gen": true
            }
        },
        {
            "name": "swagger",
            "options": {
                "swagger_path": "plugins/trace_api_plugin/trace_api.swagger.yaml",
                "swagger_dest_path": "nodeos/plugins/trace_api_plugin/api-reference",
                "disable_filters": true,
                "disable_summary_gen": true
            }
        },
        {
          "name": "doxygen_to_xml",
          "options": {
            "INPUT": "libraries/chain/include/eosio/chain/webassembly/interface.hpp plugins/blockvault_client_plugin/include/eosio/blockvault_client_plugin/blockvault.hpp"
          },
          "disable_default_filters" :true,
          "filters" : []
        },
        {
          "name": "doxybook",
          "options": {}
        }
    ]
}

{
    "eosio-code-coverage":
    {
        "exclude": [ // ignore coverage reports from source filenames matching these PCREs
            "^/build/",
            "^/coverage/",
            "^/.git/",
            "^/libraries/eos-vm/",
            "^/libraries/wasm-jit/",
            "^/node_modules/",
            "^/unittests/"
        ],
        "expect": [ // expect coverage reports from source filenames matching these PCREs
            "[.]c(pp)?([.]in)?$",
            "[.]h(pp)?([.]in)?$"
        ]
    },
    "eosio-docker-builds":
    {
        "environment":
        {
            "BUILDER_TAG": "v2.0.0"
        }
    },
    "eos-multiversion-tests":
    {
        "environment":
        {
            "IMAGE_TAG": "_1-8-0-rc2"
        },
        "configuration":
        [
            "170=v1.7.0"
        ]
    },
    // eosio-resume-from-state documentation: https://github.com/EOSIO/auto-eks-sync-nodes/blob/master/pipelines/eosio-resume-from-state/README.md
    "eosio-resume-from-state":
    {
        "test":
        [
            {
                "commit": "16074742f8cfd481b029073e6f01bb920a1bad38"
            }
        ]
    }
}

