{
  "env": {
    "es6": true,
    "node": true
  },
  "extends": [
    "eslint:recommended",
    "plugin:prettier/recommended"
  ],
  "parserOptions": {
    "ecmaVersion": 2017
  },
  "rules": {
    "no-console": "off",
    "no-var": "error",
    "prefer-const": "error",
    "no-constant-condition": [
      "error",
      {
        "checkLoops": false
      }
    ]
  }
}

{
    "name": "apache/thrift",
    "description": "Apache Thrift RPC system",
    "homepage": "http://thrift.apache.org",
    "type": "library",
    "keywords": ["RPC"],
    "license": "Apache-2.0",
    "readme": "README.md",
    "authors": [
        {
            "name": "Apache Thrift Developers",
            "email": "dev@thrift.apache.org",
            "homepage": "http://thrift.apache.org"
        }
    ],
    "support": {
        "email": "dev@thrift.apache.org",
        "issues": "https://issues.apache.org/jira/browse/THRIFT"
    },
    "require": {
        "php": "^7.1 || ^8.0"
    },
    "require-dev": {
        "phpunit/phpunit": "^7.5 || ^8.5 || ^9.5",
        "squizlabs/php_codesniffer": "3.*",
        "php-mock/php-mock-phpunit": "^2.10",
        "ext-json": "*",
        "ext-xml": "*",
        "ext-curl": "*",
        "ext-pcntl": "*"
    },
    "autoload": {
        "psr-4": {"Thrift\\": "lib/php/lib/"}
    },
    "autoload-dev": {
        "psr-4": {"Test\\Thrift\\": "lib/php/test/"}
    },
    "minimum-stability": "stable",
    "extra": {
        "branch-alias": {
            "dev-master": "1.0.x-dev"
        }
    },
    "archive": {
        "exclude": [
            "*",
            ".*",
            "!/CHANGES.md",
            "!/LICENSE",
            "!/NOTICE",
            "!/README.md",
            "!/composer.json",
            "!/lib/php/README.apache.md",
            "!/lib/php/README.md",
            "!/lib/php/lib"
        ]
    }
}

{
  "name": "apache-thrift",
  "description": "Apache Thrift D library",
  "authors": [
    "Apache Thrift Developers <dev@thrift.apache.org>"
  ],
  "homepage": "http://thrift.apache.org",
  "license": "Apache-2.0",
  "dependencies": {
    "libevent": {
      "version": "~>2.0.2"
    }
  },
  "systemDependencies": "On systems with native openssl 1.0.x use dub package openssl~>1.1, on systems with native openssl 1.1.x use dub package openssl~>2.0.3 (with build bug fix: https://github.com/D-Programming-Deimos/openssl/issues/63)",
  "configurations": [
    {
      "name": "use_openssl_1_0",
      "versions": ["use_openssl_1_0_x"],
      "dependencies": {
        "openssl": {
          "version": "~>1.1.6"
        }
      }
    },
    {
      "name": "use_openssl_1_1",
      "versions": ["use_openssl_1_1_x"],
      "dependencies": {
        "openssl": {
          "version": "~>2.0.3"
        }
      }
    }
  ],
  "targetType": "library",
  "sourcePaths": [
    "lib/d/src" 
  ],
  "importPaths": [
    "lib/d/src"
  ],
  "excludedSourceFiles": [
    "lib/d/src/thrift/index.d"
  ]
}

