static bool FreeTypeDrawBitmapV(FreeTypeGlyphInfo& FTGInfo, CAlphaBlendColor& ab, const int x, const int y)
{
	if (FTGInfo.FTGlyph->bitmap.pixel_mode != FT_PIXEL_MODE_GRAY) {
		// FT_PIXEL_MODE_GRAY
		switch (FTGInfo.FTGlyph->bitmap.pixel_mode) {
