BOOL WINAPI PathIsRelative(LPCTSTR pszPath)
{
	if (!pszPath || !*pszPath) {
		return FALSE;
	}

	const TCHAR ch1 = pszPath[0];
	const TCHAR ch2 = pszPath[1];
	if (ch1 == _T('\\') && ch2 == _T('\\')) {
		//UNC
		return FALSE;
	}
	if (ch1 == _T('\\') || (ch1 && ch2 == _T(':'))) {
		//
		return FALSE;
	}
	return TRUE;
