{
  "name": "opencc",
  "version": "1.1.9",
  "description": "Conversion between Traditional and Simplified Chinese",
  "author": "Carbo Kuo <byvoid@byvoid.com>",
  "license": "Apache-2.0",
  "main": "node/opencc.js",
  "types": "node/opencc.d.ts",
  "scripts": {
    "test": "mocha -R spec node/test.js",
    "deploy": "node-pre-gyp package && (node-pre-gyp-github publish --release || exit 0)",
    "install": "node-pre-gyp install --fallback-to-build || node-pre-gyp rebuild"
  },
  "engines": {
    "node": ">= 8.0.0"
  },
  "binary": {
    "module_name": "opencc",
    "module_path": "./build/Release/",
    "host": "https://github.com/BYVoid/OpenCC/releases/download/",
    "remote_path": "{version}"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/BYVoid/OpenCC.git"
  },
  "bugs": {
    "url": "https://github.com/BYVoid/OpenCC/issues"
  },
  "homepage": "https://github.com/BYVoid/OpenCC#readme",
  "keywords": [
    "opencc",
    "Chinese",
    "conversion",
    "unicode",
    "Simplified Chinese",
    "Traditional Chinese"
  ],
  "devDependencies": {
    "mocha": "^9.2.1",
    "node-pre-gyp-github": "^2.0.0"
  },
  "dependencies": {
    "@mapbox/node-pre-gyp": "^1.0.11",
    "nan": "^2.22.0"
  }
}

@echo off
setlocal EnableDelayedExpansion

SET VERSIONS=3.8 3.9 3.10 3.11 3.12
SET SOURCEDIR=%cd%

REM Build packages
for %%v in (%VERSIONS%) do (
    SET ENV_NAME=py%%v

    REM Create and activate environment
    cd %ROOT_DIR%
    CALL C:\Miniconda/condabin/conda.bat config --add channels conda-forge
    CALL C:\Miniconda/condabin/conda.bat config --set channel_priority strict

    CALL C:\Miniconda/condabin/conda.bat create -y -n py%%v python=%%v
    if !ERRORLEVEL! NEQ 0 (EXIT !ERRORLEVEL!)
    CALL C:\Miniconda/condabin/conda.bat activate py%%v
    if !ERRORLEVEL! NEQ 0 (EXIT !ERRORLEVEL!)
    pip install --no-cache-dir build
    if !ERRORLEVEL! NEQ 0 (EXIT !ERRORLEVEL!)

    REM Build and package
    python -m build --wheel
    if !ERRORLEVEL! NEQ 0 (EXIT !ERRORLEVEL!)

    REM Cleanup
    CALL C:\Miniconda/condabin/conda.bat deactivate
    rmdir /S /Q build OpenCC.egg-info
)

if NOT "%~1"=="testonly" (
    REM Upload to PyPI
    C:\Miniconda/condabin/conda.bat activate py3.8
    python -m pip install twine
    python -m twine upload dist/*
)
