#include <fstream>
#include <cstdlib>
#include <vector>

bool g_terminate = false;

int main(int argc, char ** argv) {
    if (argc < 5) {
        fprintf(stderr, "Usage: %s input.kbd output.kbd nf0 nf1\n", argv[0]);
