{
  "version": "1.3.12",
  "vars": {
    "PAG_GROUP": "https://github.com/libpag"
  },
  "repos": {
    "common": [
      {
        "url": "${PAG_GROUP}/vendor_tools.git",
        "commit": "ef445edee3335b3ee30bd79afea76c0419947401",
        "dir": "third_party/vendor_tools"
      },
      {
        "url": "${PAG_GROUP}/tgfx.git",
        "commit": "235ce5e99ddb33cb8679702ea6b17a7098e10023",
        "dir": "third_party/tgfx"
      },
      {
        "url": "${PAG_GROUP}/libavc.git",
        "commit": "c2bf4c84a6d39788929e59514417e819185af98e",
        "dir": "third_party/libavc"
      },
      {
        "url": "https://github.com/rttrorg/rttr.git",
        "commit": "7edbd580cfad509a3253c733e70144e36f02ecd4",
        "dir": "third_party/rttr"
      },
      {
        "url": "https://github.com/harfbuzz/harfbuzz.git",
        "commit": "f1f2be776bcd994fa9262622e1a7098a066e5cf7",
        "dir": "third_party/harfbuzz"
      },
      {
        "url": "https://github.com/lz4/lz4.git",
        "commit": "5ff839680134437dbf4678f3d0c7b371d84f4964",
        "dir": "third_party/lz4"
      }
    ]
  },
  "actions": {
    "common": [
      {
        "command": "depsync --clean",
        "dir": "third_party"
      }
    ]
  }
}

#!/usr/bin/env node
let args = process.argv.slice(2);
if (args.indexOf("-h") === -1 && args.indexOf("--help") === -1) {
    require("child_process").execSync("depsync");
}
require("./third_party/vendor_tools/lib-build");

#!/usr/bin/env node
// run 'node build_vendor -h" to print help message
require("./third_party/vendor_tools/vendor-build");

{
  "source": "third_party",
  "out": "third_party/out",
  "vendors": [
    {
      "name": "libavc",
      "cmake": {
        "targets": [
          "libavcdec"
        ],
        "platforms": [
          "ios",
          "android",
          "mac",
          "win",
          "linux"
        ]
      }
    },
    {
      "name": "harfbuzz",
      "cmake": {
        "targets": [
          "harfbuzz"
        ],
        "arguments": [
          "-DHB_HAVE_FREETYPE=OFF",
          "-DHB_HAVE_CORETEXT=OFF",
          "-DCMAKE_CXX_FLAGS=\"-DHB_LEAN -DHB_MINI -DHB_NO_UCD_UNASSIGNED -DNDEBUG -D__OPTIMIZE_SIZE__\""
        ]
      }
    },
    {
      "name": "rttr",
      "cmake": {
        "targets": [
          "rttr_core_lib"
        ],
        "arguments": [
          "-DBUILD_STATIC=TRUE"
        ],
        "includes": [
          "${SOURCE_DIR}/src/rttr",
          "${BUILD_DIR}/src/rttr"
        ],
        "platforms": [
          "mac",
          "win",
          "linux"
        ]
      }
    }
  ]
}
