/* Hex digit to 8-bit unsigned integer. */
static inline u_char
xdtoi(u_char c)
{
	if (c >= '0' && c <= '9')
		return (u_char)(c - '0');
	else if (c >= 'a' && c <= 'f')
		return (u_char)(c - 'a' + 10);
	else
		return (u_char)(c - 'A' + 10);
}

int
__pcap_atoin(const char *s, bpf_u_int32 *addr)
{
	u_int n;
	int len;

	*addr = 0;
	len = 0;
	for (;;) {
		n = 0;
		while (*s && *s != '.') {
			if (n > 25) {
				/* The result will be > 255 */
				return -1;
			}
			n = n * 10 + *s++ - '0';
		}
		if (n > 255)
			return -1;
		*addr <<= 8;
		*addr |= n & 0xff;
		len += 8;
		if (*s == '\0')
			return len;
		++s;
	}
	/* NOTREACHED */
}

/*
 * If 's' is not a string that is a well-formed DECnet address (aa.nnnn),
 * return zero.  Otherwise parse the address into the low 16 bits of 'addr'
 * and return a non-zero.  The binary DECnet address consists of a 6-bit area
 * number and a 10-bit node number; neither area 0 nor node 0 are valid for
 * normal addressing purposes, but either can appear on the wire.
 */
int
__pcap_atodn(const char *s, bpf_u_int32 *addr)
{
#define AREASHIFT 10
#define AREAMASK 0176000
#define NODEMASK 01777

	/* Initialize to squelch a compiler warning only. */
	u_int node = 0, area = 0;
	/*
	 *               +--+             +--+
	 *               |  |             |  |
	 *               v  |             v  |
	 * --> START --> AREA --> DOT --> NODE -->
	 *       |          |     |        |
	 *       |          v     v        |
	 *       +--------> INVALID <------+
	 */
	enum {
		START,
		AREA,
		DOT,
		NODE,
		INVALID
	} fsm_state = START;

	while (*s) {
		switch (fsm_state) {
		case START:
			if (PCAP_ISDIGIT(*s)) {
				area = *s - '0';
				fsm_state = AREA;
				break;
			}
			fsm_state = INVALID;
			break;
		case AREA:
			if (*s == '.') {
				fsm_state = DOT;
				break;
			}
			if (PCAP_ISDIGIT(*s)) {
				area = area * 10 + *s - '0';
				if (area <= AREAMASK >> AREASHIFT)
					break;
			}
			fsm_state = INVALID;
			break;
		case DOT:
			if (PCAP_ISDIGIT(*s)) {
				node = *s - '0';
				fsm_state = NODE;
				break;
			}
			fsm_state = INVALID;
			break;
		case NODE:
			if (PCAP_ISDIGIT(*s)) {
				node = node * 10 + *s - '0';
				if (node <= NODEMASK)
					break;
			}
			fsm_state = INVALID;
			break;
		case INVALID:
			return 0;
		} /* switch */
		s++;
	} /* while */
	/*
	 * This condition is false if the string comes from the lexer, but
	 * let's not depend on that.
	 */
	if (fsm_state != NODE)
		return 0;

	*addr = area << AREASHIFT | node;
	return(32);
}

/*
 * Convert 's', which can have the one of the forms:
 *
 *	"xx:xx:xx:xx:xx:xx"
 *	"xx.xx.xx.xx.xx.xx"
 *	"xx-xx-xx-xx-xx-xx"
 *	"xxxx.xxxx.xxxx"
 *	"xxxxxxxxxxxx"
 *
 * (or various mixes of ':', '.', and '-') into a new
 * ethernet address.  Assumes 's' is well formed.
 */
u_char *
pcap_ether_aton(const char *s)
{
	register u_char *ep, *e;
	register u_char d;

	e = ep = (u_char *)malloc(6);
	if (e == NULL)
		return (NULL);

	while (*s) {
		if (*s == ':' || *s == '.' || *s == '-')
			s += 1;
		d = xdtoi(*s++);
		if (PCAP_ISXDIGIT(*s)) {
			d <<= 4;
			d |= xdtoi(*s++);
		}
		*ep++ = d;
	}

	return (e);
}

#ifndef HAVE_ETHER_HOSTTON
/*
 * Roll our own.
 *
 * This should be thread-safe, as we define the static variables
 * we use to be thread-local, and as pcap_next_etherent() does so
 * as well.
 */
u_char *
pcap_ether_hostton(const char *name)
{
	register struct pcap_etherent *ep;
	register u_char *ap;
	static thread_local FILE *fp = NULL;
	static thread_local int init = 0;

	if (!init) {
		fp = fopen(PCAP_ETHERS_FILE, "r");
		++init;
		if (fp == NULL)
			return (NULL);
	} else if (fp == NULL)
		return (NULL);
	else
		rewind(fp);

	while ((ep = pcap_next_etherent(fp)) != NULL) {
		if (strcmp(ep->name, name) == 0) {
			ap = (u_char *)malloc(6);
			if (ap != NULL) {
				memcpy(ap, ep->addr, 6);
				return (ap);
			}
			break;
		}
	}
	return (NULL);
}
#else
/*
 * Use the OS-supplied routine.
 * This *should* be thread-safe; the API doesn't have a static buffer.
 */
u_char *
pcap_ether_hostton(const char *name)
{
	register u_char *ap;
	u_char a[6];
	char namebuf[1024];

	/*
	 * In AIX 7.1 and 7.2: int ether_hostton(char *, struct ether_addr *);
	 */
	pcapint_strlcpy(namebuf, name, sizeof(namebuf));
	ap = NULL;
	if (ether_hostton(namebuf, (struct ether_addr *)a) == 0) {
		ap = (u_char *)malloc(6);
		if (ap != NULL)
			memcpy((char *)ap, (char *)a, 6);
	}
	return (ap);
}
#endif

