    case DW_EH_PE_sdata4: return @"sdata4";
    case DW_EH_PE_sdata8: return @"sdata8";

    case DW_EH_PE_absptr | DW_EH_PE_pcrel: return @"pcrel";
    case DW_EH_PE_uleb128 | DW_EH_PE_pcrel: return @"pcrel uleb128";
