  {4, 0xe0, 0x8f, 0xc0, 0x0c},              // add	ip, pc, ip
  {4, 0xe5, 0x2d, 0xc0, 0x04},              // str ip, [sp, #-4]!
  // jump through _fast_lazy_bind
  {4, 0xe5, 0x9f, 0xc0, 0x08},              // ldr	ip, L2
  {4, 0xe0, 0x8f, 0xc0, 0x0c},              // add	ip, pc, ip
