      { "help.", 5, "u", "u", "" },     { " ", 1, "u", "u", "" },
      { "jk", 2, "bu", "bu", "" },      { "/", 1, "u", "u", "" },
      { du_arrows, 2, "bu", "bu", "" }, { " ", 1, "u", "u", "" },
