    entries_nb = sizeof(generic_entries) / sizeof(help_entry_t);
    entries_total_nb += help_add_entries(win,
                                         term,
                                         toggles,
                                         help_items_da,
                                         generic_entries,
                                         entries_nb);

    entries_nb = sizeof(trailer_entries) / sizeof(help_entry_t);
