    {
        src/anim_mon_front_pics.o(.rodata);
    } > ROM =0

    gfx_data __gfx_load_address :
    ALIGN(4)
    {
        src/graphics.o(.rodata);
    } > ROM =0

    extra :
    ALIGN(4)
    {
        src/*.o(.text);
        src/*.o(.rodata);
        data/*.o(.rodata);
    } > ROM = 0

    /* DWARF debug sections.
