#ifdef HAVE_CONFIG_H
#include "i3blocks-config.h"
#endif

#include <getopt.h>
#include <stdio.h>
#include <stdlib.h>

#include "bar.h"
#include "log.h"
#include "sys.h"

unsigned int log_level;

int main(int argc, char *argv[])
{
	char *output = NULL;
	char *path = NULL;
	bool term;
	int c;

	while (c = getopt(argc, argv, "c:o:vhV"), c != -1) {
		switch (c) {
		case 'c':
			path = optarg;
			break;
		case 'o':
			output = optarg;
			break;
		case 'v':
			log_level++;
			break;
		case 'h':
			printf("Usage: %s [-c <configfile>] [-o <output>] [-v] [-h] [-V]\n", argv[0]);
			return EXIT_SUCCESS;
		case 'V':
			printf(PACKAGE_STRING " Â© 2014-2019 Vivien Didelot and contributors\n");
			return EXIT_SUCCESS;
		default:
			error("Try '%s -h' for more information.", argv[0]);
			return EXIT_FAILURE;
		}
	}

	term = !sys_isatty(STDOUT_FILENO);
	if (output)
		term = !strcmp(output, "term");

	if (bar_init(term, path))
		return EXIT_FAILURE;

	return EXIT_SUCCESS;
}

