    // label patterns with "qemu-nut-builder" and/or "qemu-nut-builder:alldrv".
    dynacfgPipeline.disableSlowBuildCIBuild_QEMU = true
    //if ( env?.BRANCH_NAME ==~ /master|main|stable|.*qemu.*/ ) {
    if ( env?.BRANCH_NAME ==~ /.*qemu.*/ ) {
