int main(int argc, char *argv[])
{
static int auswahl;
static int index;
static FILE *fh_out;

static char *wordlistinname = NULL;
static char *wordlistoutname = NULL;

static const char *short_options = "i:o:hv";
static const struct option long_options[] =
{
	{"straight",			no_argument,		NULL,	HCX_STRAIGHT},
	{"digit",			no_argument,		NULL,	HCX_DIGIT},
	{"xdigit",			no_argument,		NULL,	HCX_XDIGIT},
	{"lower",			no_argument,		NULL,	HCX_LOWER},
	{"upper",			no_argument,		NULL,	HCX_UPPER},
	{"capital",			no_argument,		NULL,	HCX_CAPITAL},
	{"length",			required_argument,	NULL,	HCX_SWEEP_LEN},
	{"version",			no_argument,		NULL,	HCX_VERSION},
	{"help",			no_argument,		NULL,	HCX_HELP},
	{NULL,				0,			NULL,	0}
};

wantstraight = false;
wantdigit = false;
wantxdigit = false;
wantlower = false;
wantupper = false;
wantcapital = false;
sweeplen = 0;

auswahl = -1;
index = 0;
optind = 1;
optopt = 0;

while((auswahl = getopt_long (argc, argv, short_options, long_options, &index)) != -1)
	{
	switch (auswahl)
		{
		case HCX_INPUT_WORDLIST:
		wordlistinname = optarg;
		break;

		case HCX_OUTPUT_WORDLIST:
		wordlistoutname = optarg;
		break;

		case HCX_STRAIGHT:
		wantstraight = true;
		break;

		case HCX_DIGIT:
		wantdigit = true;
		break;

		case HCX_XDIGIT:
		wantxdigit = true;
		break;

		case HCX_LOWER:
		wantlower = true;
		break;

		case HCX_UPPER:
		wantupper = true;
		break;

		case HCX_CAPITAL:
		wantcapital = true;
		break;

		case HCX_SWEEP_LEN:
		sweeplen = strtol(optarg, NULL, 10);
		if((sweeplen < 8) || (sweeplen > 32))
			{
			fprintf(stderr, "only 8...32 allowed\n");
			exit(EXIT_FAILURE);
			}
		break;

		case HCX_HELP:
		usage(basename(argv[0]));
		break;

		case HCX_VERSION:
		version(basename(argv[0]));
		break;

		case '?':
		usageerror(basename(argv[0]));
		break;
		}
	}

if(argc < 2)
	{
	fprintf(stderr, "no option selected\n");
	return EXIT_SUCCESS;
	}

if(wordlistoutname != 0)
	{
	if((fh_out = fopen(wordlistoutname, "w")) == NULL)
		{
		perror("failed to open output file");
		exit(EXIT_FAILURE);
		}
	}
else
	{
	fh_out = stdout;
	}

if(wordlistinname != NULL)
	{
	processwordlist(wordlistinname, fh_out);
	}

if(wordlistoutname != 0)
	{
	fclose(fh_out);
	}

return EXIT_SUCCESS;
}
