///////////////////////////////////////////////////////////////////////////
//
//	SDL_ALPlaySound() - Plays the specified sound on the AdLib card
//
///////////////////////////////////////////////////////////////////////////
#ifdef	_MUSE_
void
#else
static void
#endif
SDL_ALPlaySound(AdLibSound far *sound)
{
	byte		c,m;
	Instrument	far *inst;

asm	pushf
asm	cli

	SDL_ALStopSound();

	alLengthLeft = sound->common.length;
	alSound = sound->data;
	alBlock = ((sound->block & 7) << 2) | 0x20;
	inst = &sound->inst;

	if (!(inst->mSus | inst->cSus))
		Quit("SDL_ALPlaySound() - Seriously suspicious instrument");

	m = modifiers[0];
	c = carriers[0];
	alOut(m + alChar,inst->mChar);
	alOut(m + alScale,inst->mScale);
	alOut(m + alAttack,inst->mAttack);
	alOut(m + alSus,inst->mSus);
	alOut(m + alWave,inst->mWave);
	alOut(c + alChar,inst->cChar);
	alOut(c + alScale,inst->cScale);
	alOut(c + alAttack,inst->cAttack);
	alOut(c + alSus,inst->cSus);
	alOut(c + alWave,inst->cWave);

asm	popf
}

///////////////////////////////////////////////////////////////////////////
//
// 	SDL_ALSoundService() - Plays the next sample out through the AdLib card
//
///////////////////////////////////////////////////////////////////////////
static void
SDL_ALSoundService(void)
{
	byte	s;

	if (alSound)
	{
		s = *alSound++;
		if (!s)
			alOut(alFreqH + 0,0);
		else
		{
			alOut(alFreqL + 0,s);
			alOut(alFreqH + 0,alBlock);
		}

		if (!(--alLengthLeft))
		{
			(long)alSound = 0;
			alOut(alFreqH + 0,0);
			SDL_SoundFinished();
		}
	}
}

///////////////////////////////////////////////////////////////////////////
//
//	SDL_SelectMeasure() - sets up sequencing variables for a given track
//
///////////////////////////////////////////////////////////////////////////
static void
SDL_SelectMeasure(ActiveTrack *track)
{
	track->seq = track->moods[track->mood];
	track->nextevent = 0;
}

///////////////////////////////////////////////////////////////////////////
//
//	My AdLib interrupt service routine
//	Called 140 times/second by SDL_t0Service()
//
///////////////////////////////////////////////////////////////////////////
static void
SDL_ALService(void)
{
	boolean		update;
	word		*seq;
	longword	next;
	int			i;
	Instrument	*ins;
	ActiveTrack	*track;

	if (!sqActive)
		return;

	for (i = 1;i <= sqMaxTracks;i++)
	{
		if (!((track = tracks[i - 1]) && (seq = track->seq)))
			continue;

		if ((sqMode != sqmode_Normal) && (sqFadeStep < sqMaxFade))
		{
			// DEBUG - not done
			sqFadeStep++;
		}

		next = track->nextevent;
		while (next <= TimeCount)
		{
			update = true;
			switch (*seq++)
			{
			case sev_NoteOff:
				alOut(alFreqH + i,0);	/* Turn the note off */
				next = TimeCount + *seq;
				break;
			case sev_NoteOn:
			case sev_NotePitch:
				next = TimeCount + *seq++;
				alOut(alFreqL + i,*seq);		/* Program low 8 bits of freq */
				alOut(alFreqH + i,(*seq >> 8) | 0x20);	/* & hi 5 bits and turn note on */
				break;
			case sev_NewInst:
				ins = (Instrument *)seq;
				SDL_SetInstrument(i,ins++);
				seq = (word *)(ins);
				next = TimeCount + *seq;
				break;
			case sev_NewPerc:
				// DEBUG - write this
				break;
			case sev_PercOn:
				alFXReg |= (1 << *seq);
				alOut(alEffects,alFXReg);
				break;
			case sev_PercOff:
				alFXReg &= ~(1 << *seq);
				alOut(alEffects,alFXReg);
				break;
			case sev_SeqEnd:
				SDL_SelectMeasure(track);
				next = track->nextevent;
				if (!(seq = track->seq))
					continue;
				update = false;
				break;
			case sev_Null:
				next = TimeCount + *seq;
				break;
			default:
				break;
			}
			if (update)
			{
				track->seq = ++seq;
				track->nextevent = next;
			}
		}
	}
}

