====================
=
= TaterThink
=
====================
*/

void TaterThink (objtype *ob)
{
	int delta;

	if (ob->top > player->bottom || ob->bottom < player->top)
		return;

	if ( ob->xdir == -1 )
	{
		delta = ob->left - player->right;
		if (delta > TILEGLOBAL)
			return;
		if (delta < -8*PIXGLOBAL)
		{
			ob->xdir = 1;
			return;
		}
		SD_PlaySound (TATERSWINGSND);
		ob->state = &s_taterattack1;
		return;
	}
	else
	{
		delta = player->left - ob->right;
		if (delta > TILEGLOBAL)
			return;
		if (delta < -8*PIXGLOBAL)
		{
			ob->xdir = -1;
			return;
		}
		SD_PlaySound (TATERSWINGSND);
		ob->state = &s_taterattack1;
		return;
	}
}


