void SquasherThink (objtype *ob)
{
	int delta;

	if ( abs(ob->y - player->y) > 3*TILEGLOBAL )
	{
		if (US_RndT()<8)
			ob->xdir = -ob->xdir;		// turn randomly
		return;
	}


	delta = player->x - ob->x;

	if ( ob->xdir == -1 )
	{
		if (delta < -6*TILEGLOBAL)
			return;
		if (delta > 8*PIXGLOBAL)
		{
			ob->xdir = 1;
			return;
		}
	}
	else
	{
		if (delta > 6*TILEGLOBAL)
			return;
		if (delta < -8*PIXGLOBAL)
		{
			ob->xdir = 1;
			return;
		}
	}

	ob->yspeed = -SPDSQUASHLEAPY;
	ob->xspeed = delta/60;
	ob->state = &s_squasherjump1;
}

