void	PowerCount (objtype *ob)
{
	ob->temp2+=tics;

	ob->shapenum = 0;

	if (ob->temp2 > POWERCOUNT)
		RemoveObj(ob);
}


/*
============================
=
= CalcSingleGravity
=
============================
*/

void	CalcSingleGravity (void)
{
	unsigned	speed;
	long	i;
//
// only accelerate on odd tics, because of limited precision
//
	speed = 0;
	singlegravity = 0;
	for (i=lasttimecount-tics;i<lasttimecount;i++)
	{
		if (i&1)
		{
			speed+=ACCGRAVITY;
			if (speed>SPDMAXY)
			  speed=SPDMAXY;
		}
		singlegravity+=speed;
	}

	singlegravity/=2;
}


/*
============================
=
= PowerContact
=
============================
*/

void	PowerContact (objtype *ob, objtype *hit)
{
	unsigned	x,y,yspot,xspot;

	switch (hit->obclass)
	{
	case	broccoobj:
	case	tomatobj:
	case	carrotobj:
	case	celeryobj:
	case	asparobj:
	case	taterobj:
	case	frenchyobj:
	case	squashobj:
	case	apelobj:
	case	peapodobj:
	case	peabrainobj:
		ChangeToFlower (hit);
		RemoveObj (ob);
		break;
	case	boobusobj:
		if (!--hit->temp4)
		{
		// killed boobus
			GivePoints (50000);
			GivePoints (50000);
			hit->obclass = inertobj;
			ChangeState (hit,&s_boobusdie);
			hit->temp1 = 0;		// death count
		}
		SD_PlaySound (BOMBBOOMSND);
		ChangeState (ob,&s_bombexplode);
		break;
	}
}


