void CheckKeys (void)
{
	if (screenfaded)			// don't do anything with a faded screen
		return;

//
// space for status screen
//
	if (Keyboard[sc_Space])
	{
		StatusWindow ();
		IN_ClearKeysDown();
		RF_ForceRefresh();
		lasttimecount = TimeCount;
	}

//
// pause key wierdness can't be checked as a scan code
//
	if (Paused)
	{
		VW_FixRefreshBuffer ();
		US_CenterWindow (8,3);
		US_PrintCentered ("PAUSED");
		VW_UpdateScreen ();
		IN_Ack();
		RF_ForceRefresh ();
		Paused = false;
	}

//
// F1-F7/ESC to enter control panel
//
	if ( (LastScan >= sc_F1 && LastScan <= sc_F7) || LastScan == sc_Escape)
	{
		VW_FixRefreshBuffer ();
		US_CenterWindow (20,8);
		US_CPrint ("Loading");
		VW_UpdateScreen ();
		US_ControlPanel();
		IN_ClearKeysDown();
		if (restartgame)
			playstate = resetgame;
		else if (!loadedgame)
			RF_ForceRefresh();		// don't refresh if loading a new game

		lasttimecount = TimeCount;
	}

//
// F10-? debug keys
//
	if (Keyboard[sc_F10] && DebugKeys() )
	{
		RF_ForceRefresh();
		lasttimecount = TimeCount;
	}

}

