VOID CALLBACK ShowOptions_RunDLLW( HWND hWnd, HINSTANCE hInstance,
                                   PWSTR pszCmdLine, INT nCmdShow )
{
	HASHCHECKOPTIONS opt;
	OptionsDialog(hWnd, &opt);
