                bus->buf[bus->buf_b++] = 0x00;          /* reserved */
                bus->buf[bus->buf_b++] = 0x02;          /* page length */
                bus->buf[bus->buf_b++] = 0x00;          /* page 0 is supported. */
                bus->buf[bus->buf_b++] = 0x80;          /* page 0x80 is supported. */
