        int mask = bam_str2flag(argv[i]);
        if ( mask<0 ) { print_error("flags", "Could not parse \"%s\"", argv[i]); usage(stderr); return 1; }
        char *str = bam_flag2str(mask);
        printf("0x%x\t%d\t%s\n", mask, mask, str);
        free(str);
    }
    return 0;
}

