	int port; /* port, or zero */
#ifndef _WIN32
	char *unixsocket; /* unix domain socket or NULL */
#endif
	char *path; /* path, or "". */
	char *query; /* query, or NULL */
	char *fragment; /* fragment or NULL */
};

