    sprintf(buffer2, "n,,%.200s", clientfirstmessagebare);
    hydra_tobase64((unsigned char *)buffer2, strlen(buffer2), sizeof(buffer2));
    snprintf(buffer, sizeof(buffer), "%s\r\n", buffer2);

    if (hydra_send(s, buffer, strlen(buffer), 0) < 0) {
