# MLib macros we can't do much about.
//-V:M_LET:1048,1044
//-V:M_EACH:1048,1044
//-V:ARRAY_DEF:760,747,568,776,729,712,654
//-V:LIST_DEF:760,747,568,712,729,654,776
//-V:BPTREE_DEF2:779,1086,557,773,512
//-V:DICT_DEF2:779,524,776,760,1044,1001,729,590,568,747,685
//-V:ALGO_DEF:1048,747,1044
//-V:TUPLE_DEF2:524,590,1001,760

# Non-severe malloc/null pointer deref warnings
//-V::522:2,3

# Warning about headers with copyleft license
//-V::1042

# Potentially null argument warnings
//-V:memset:575
//-V:memcpy:575
//-V:memcmp:575
//-V:strlen:575
//-V:strcpy:575
//-V:strncpy:575
//-V:strchr:575

# For loop warning on M_FOREACH
//-V:for:1044

# Bitwise OR
//-V:bit:792

# Do not complain about similar code
//-V::525

# Common embedded development pointer operations
//-V::566
//-V::1032

# Warnings about length mismatch
//-V:property_value_out:666

# Model-related warnings
//-V:with_view_model:1044,1048

# Functions that always return the same error code
//-V:picopass_device_decrypt:1048

# Examples
//V_EXCLUDE_PATH applications/examples/

#!/bin/sh

# shellcheck disable=SC2086 source=/dev/null
# unofficial strict mode
set -eu;

# private variables
N_CORES="$(getconf _NPROCESSORS_ONLN)";
N_GIT_THREADS="$(($N_CORES * 2))";
SCRIPT_PATH="$(cd "$(dirname "$0")" && pwd -P)";
SCONS_DEFAULT_FLAGS="--warn=target-not-built";
SCONS_EP="python3 -m SCons";

# public variables
FBT_NO_SYNC="${FBT_NO_SYNC:-""}";
FBT_TOOLCHAIN_PATH="${FBT_TOOLCHAIN_PATH:-$SCRIPT_PATH}";
FBT_VERBOSE="${FBT_VERBOSE:-""}";
FBT_GIT_SUBMODULE_SHALLOW="${FBT_GIT_SUBMODULE_SHALLOW:-""}";

FBT_VERBOSE="$FBT_VERBOSE" . "$SCRIPT_PATH/scripts/toolchain/fbtenv.sh";

if [ -z "$FBT_VERBOSE" ]; then
    SCONS_DEFAULT_FLAGS="$SCONS_DEFAULT_FLAGS -Q";
fi

if [ -z "$FBT_NO_SYNC" ]; then
    if [ ! -e "$SCRIPT_PATH/.git" ]; then
        echo "\".git\" directory not found, please clone repo via \"git clone\"";
        exit 1;
    fi
    _FBT_CLONE_FLAGS="--jobs $N_GIT_THREADS";
    if [ ! -z "$FBT_GIT_SUBMODULE_SHALLOW" ]; then
        _FBT_CLONE_FLAGS="$_FBT_CLONE_FLAGS --depth 1";
    fi

    git submodule update --init --recursive --recursive $_FBT_CLONE_FLAGS;
fi

$SCONS_EP $SCONS_DEFAULT_FLAGS "$@"

@echo off
call "%~dp0scripts\toolchain\fbtenv.cmd" env || exit /b

set SCONS_EP=python -m SCons

if [%FBT_NO_SYNC%] == [] (
    set _FBT_CLONE_FLAGS=--jobs %NUMBER_OF_PROCESSORS%
    if not [%FBT_GIT_SUBMODULE_SHALLOW%] == [] (
        set _FBT_CLONE_FLAGS=%_FBT_CLONE_FLAGS% --depth 1
    )
    if exist ".git" (
        git submodule update --init --recursive --recursive %_FBT_CLONE_FLAGS%
        if %ERRORLEVEL% neq 0 (
            echo Failed to update submodules, set FBT_NO_SYNC to skip
            exit /b 1
        )
    ) else (
        echo .git not found, please clone repo with "git clone"
        exit /b 1
    )
)

set "SCONS_DEFAULT_FLAGS=--warn=target-not-built"

if not defined FBT_VERBOSE (
    set "SCONS_DEFAULT_FLAGS=%SCONS_DEFAULT_FLAGS% -Q"
)

%SCONS_EP% %SCONS_DEFAULT_FLAGS% %*
